import * as moment from "moment";
import {Moment} from "moment";
import Parser from '../parser';
import {ParsedResult} from "../../result";
import {ParseOptions} from "../../chrono";
import {DAY, HOUR, MERIDIEM, MILLISECOND, MINUTE, MONTH, SECOND, YEAR} from "../../constants";

export default class DECasualDateParser extends Parser {
    private PATTERN: RegExp = new RegExp(
        '(\\W|^)(' +
        'jetzt|' +
        '(?:heute|diesen)\\s*(morgen|vormittag|mittag|nachmittag|abend)|' +
        '(?:heute|diese)\\s*nacht|' +
        'heute|' +
        '(?:(?:ü|ue)ber)?morgen(?:\\s*(morgen|vormittag|mittag|nachmittag|abend|nacht))?|' +
        '(?:vor)?gestern(?:\\s*(morgen|vormittag|mittag|nachmittag|abend|nacht))?|' +
        'letzte\\s*nacht' +
        ')(?=\\W|$)', 'i');

    private OTHER_PATTERNS: RegExp[] = [
        /(?:heute|diese)\s*nacht/,
        /^(?:ü|ue)bermorgen/,
        /^morgen/,
        /^gestern/,
        /^vorgestern/,
        /letzte\s*nacht/
    ];

    private TAG: string = 'DECasualDateParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length),
            index,
            ref
        });

        const matchedText: string = result.text.toLowerCase();
        let refMoment: Moment = moment(ref);
        let startMoment: Moment = refMoment.clone();

        if (this.OTHER_PATTERNS[0].test(matchedText)) {
            // Normally means this coming midnight
            result.start.imply(HOUR, 22);
            result.start.imply(MERIDIEM, 1);
        } else if (this.OTHER_PATTERNS[1].test(matchedText)) {
            startMoment.add(refMoment.hour() > 1 ? 2 : 1, 'day');
        } else if (this.OTHER_PATTERNS[2].test(matchedText)) {
            // Check not "Tomorrow" on late night
            if (refMoment.hour() > 1) {
                startMoment.add(1, 'day');
            }
        } else if (this.OTHER_PATTERNS[3].test(matchedText)) {
            startMoment.add(-1, 'day');
        } else if (this.OTHER_PATTERNS[4].test(matchedText)) {
            startMoment.add(-2, 'day');
        } else if (this.OTHER_PATTERNS[5].test(matchedText)) {
            result.start.imply(HOUR, 0);
            if (refMoment.hour() > 6) {
                startMoment.add(-1, 'day');
            }
        } else if (matchedText === 'jetzt') {
            result.start.imply(HOUR, refMoment.hour());
            result.start.imply(MINUTE, refMoment.minute());
            result.start.imply(SECOND, refMoment.second());
            result.start.imply(MILLISECOND, refMoment.millisecond());
        }

        const secondMatch: string = match[3] || match[4] || match[5];
        if (secondMatch) {
            switch (secondMatch.toLowerCase()) {
                case 'morgen':
                    result.start.imply(HOUR, 6);
                    break;
                case 'vormittag':
                    result.start.imply(HOUR, 9);
                    break;
                case 'mittag':
                    result.start.imply(HOUR, 12);
                    break;
                case 'nachmittag':
                    result.start.imply(HOUR, 15);
                    result.start.imply(MERIDIEM, 1);
                    break;
                case 'abend':
                    result.start.imply(HOUR, 18);
                    result.start.imply(MERIDIEM, 1);
                    break;
                case 'nacht':
                    result.start.imply(HOUR, 0);
                    break;
            }
        }

        result.start.assign(DAY, startMoment.date());
        result.start.assign(MONTH, startMoment.month() + 1);
        result.start.assign(YEAR, startMoment.year());
        result.tags[this.TAG] = true;

        return result;
    }
}

import * as moment from 'moment';
import Parser from '../parser';
import {ParsedResult} from "../../result";
import {ParseOptions} from "../../chrono";
import {UnitOfTime} from "../../constants";
import {INTEGER_WORDS_PATTERN, matchInteger, matchUnit} from '../../utils/DE';
import {deadlineCalculations} from "../../utils/general";

export default class DEDeadlineFormatParser extends Parser {
    private PATTERN: RegExp = new RegExp('(\\W|^)' +
        '(in|nach)\\s*' +
        '(' + INTEGER_WORDS_PATTERN + '|[0-9]+|einigen|eine[rm]\\s*halben|eine[rm])\\s*' +
        '(sekunden?|min(?:ute)?n?|stunden?|tag(?:en)?|wochen?|monat(?:en)?|jahr(?:en)?)\\s*' +
        '(?=\\W|$)', 'i'
    );

    private STRICT_PATTERN: RegExp = new RegExp('(\\W|^)' +
        '(in|nach)\\s*' +
        '(' + INTEGER_WORDS_PATTERN + '|[0-9]+|eine(?:r|m)?)\\s*' +
        '(sekunden?|minuten?|stunden?|tag(?:en)?)\\s*' +
        '(?=\\W|$)', 'i'
    );

    private TAG: string = 'DEDeadlineFormatParser';

    pattern() {
        return this.isStrictMode() ? this.STRICT_PATTERN : this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const matchedText: string = match[0].substr(match[1].length, match[0].length - match[1].length);

        const result: ParsedResult = new ParsedResult({
            text: matchedText,
            index,
            ref
        });

        result.tags[this.TAG] = true;
        let num: number = matchInteger(match[3].toLowerCase());
        const matchedUnit: UnitOfTime | undefined = matchUnit(match[4].toLowerCase());

        if (num && matchedUnit && deadlineCalculations(num, matchedUnit, result, moment(ref))) {
            return result;
        }

        return null;
    }
}

import Parser from '../parser';
import {ParsedResult} from "../../result";
import {ParseOptions} from "../../chrono";
import {DAY, MONTH, WEEKDAY, YEAR} from "../../constants";
import {MONTH_OFFSET, WEEKDAY_OFFSET, yearCalculation} from '../../utils/DE';
import {getAppropriateYear} from "../../utils/general";

export default class DEMonthNameLittleEndianParser extends Parser {
    private PATTERN: RegExp = new RegExp('(\\W|^)' +
        '(?:am\\s*?)?' +
        '(?:(Sonntag|Montag|Dienstag|Mittwoch|Donnerstag|Freitag|Samstag|So|Mo|Di|Mi|Do|Fr|Sa)\\s*,?\\s*)?' +
        '(?:den\\s*)?' +
        '([0-9]{1,2})\\.' +
        '(?:\\s*(?:bis(?:\\s*(?:am|zum))?|\\-|\\–|\\s)\\s*([0-9]{1,2})\\.)?\\s*' +
        '(Jan(?:uar|\\.)?|Feb(?:ruar|\\.)?|Mär(?:z|\\.)?|Maerz|Mrz\\.?|Apr(?:il|\\.)?|Mai|Jun(?:i|\\.)?|Jul(?:i|\\.)?|Aug(?:ust|\\.)?|Sep(?:t|t\\.|tember|\\.)?|Okt(?:ober|\\.)?|Nov(?:ember|\\.)?|Dez(?:ember|\\.)?)' +
        '(?:' +
        ',?\\s*([0-9]{1,4}(?![^\\s]\\d))' +
        '(\\s*[vn]\\.?\\s*C(?:hr)?\\.?)?' +
        ')?' +
        '(?=\\W|$)', 'i'
    );

    private WEEKDAY_GROUP: number = 2;
    private DATE_GROUP: number = 3;
    private DATE_TO_GROUP: number = 4;
    private MONTH_NAME_GROUP: number = 5;
    private YEAR_GROUP: number = 6;
    private YEAR_BE_GROUP: number = 7;


    private TAG: string = 'DEMonthNameLittleEndianParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length, match[0].length - match[1].length),
            index: match.index + match[1].length,
            ref
        });

        const month: number = MONTH_OFFSET[match[this.MONTH_NAME_GROUP].toLowerCase()];

        const day: number = parseInt(match[this.DATE_GROUP], 10);

        result.start.assign(DAY, day);
        result.start.assign(MONTH, month);

        let year: number | null = yearCalculation(match[this.YEAR_GROUP], match[this.YEAR_BE_GROUP]);

        if (year !== null) {
            result.start.assign(YEAR, year);
        } else {
            getAppropriateYear(result.start, ref);
        }

        // Weekday component
        if (match[this.WEEKDAY_GROUP]) {
            result.start.assign(WEEKDAY, WEEKDAY_OFFSET[match[this.WEEKDAY_GROUP].toLowerCase()]);
        }

        // Text can be 'range' value. Such as '12 - 13 January 2012'
        if (match[this.DATE_TO_GROUP]) {
            result.end = result.start.clone();
            result.end.assign(DAY, parseInt(match[this.DATE_TO_GROUP], 10));
        }

        result.tags[this.TAG] = true;
        return result;
    }

}

import Parser from '../parser';
import {ParsedResult} from "../../result";
import {ParseOptions} from "../../chrono";
import {DAY, MONTH, YEAR} from "../../constants";
import {MONTH_OFFSET, yearCalculation} from '../../utils/DE';
import {getAppropriateYear} from "../../utils/general";

export default class DEMonthNameParser extends Parser {
    private PATTERN: RegExp = new RegExp('(^|\\D\\s+|[^\\w\\s])' +
        '(Jan\\.?|Januar|Feb\\.?|Februar|Mär\\.?|M(?:ä|ae)rz|Mrz\\.?|Apr\\.?|April|Mai\\.?|Jun\\.?|Juni|Jul\\.?|Juli|Aug\\.?|August|Sep\\.?|Sept\\.?|September|Okt\\.?|Oktober|Nov\\.?|November|Dez\\.?|Dezember)' +
        '\\s*' +
        '(?:' +
        ',?\\s*(?:([0-9]{4})(\\s*[vn]\\.?\\s*C(?:hr)?\\.?)?|([0-9]{1,4})\\s*([vn]\\.?\\s*C(?:hr)?\\.?))' +
        ')?' +
        '(?=[^\\s\\w]|$)', 'i');

    private MONTH_NAME_GROUP: number = 2;
    private YEAR_GROUP: number = 3;
    private YEAR_BE_GROUP: number = 4;
    private YEAR_GROUP2: number = 5;
    private YEAR_BE_GROUP2: number = 6;

    private TAG: string = 'DEMonthNameParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length, match[0].length - match[1].length),
            index: match.index + match[1].length,
            ref: ref,
        });


        const month = MONTH_OFFSET[match[this.MONTH_NAME_GROUP].toLowerCase()];

        result.start.imply(DAY, 1);
        result.start.assign(MONTH, month);

        let year: number | null = yearCalculation(match[this.YEAR_GROUP] || match[this.YEAR_GROUP2], match[this.YEAR_BE_GROUP] || match[this.YEAR_BE_GROUP2]);

        if (year !== null) {
            result.start.assign(YEAR, year);
        } else {
            getAppropriateYear(result.start, ref);
        }

        result.tags[this.TAG] = true;
        return result;
    }
}

/*
    Date format with slash "/" (also "-" and ".") between numbers
    - Tuesday 11/3/2015
    - 11/3/2015
    - 11/3
*/
import Parser from '../parser';
import {ParsedResult} from "../../result";
import {ParseOptions} from "../../chrono";
import {DAY, MONTH, WEEKDAY, YEAR} from "../../constants";
import {WEEKDAY_OFFSET} from '../../utils/DE';
import * as moment from 'moment';

export default class DESlashDateFormatParser extends Parser {
    private PATTERN: RegExp = new RegExp('(\\W|^)' +
        '(?:' +
        '(?:am\\s*?)?' +
        '((?:sonntag|so|montag|mo|dienstag|di|mittwoch|mi|donnerstag|do|freitag|fr|samstag|sa))' +
        '\\s*\\,?\\s*' +
        '(?:den\\s*)?' +
        ')?' +
        '([0-3]{0,1}[0-9]{1})[\\/\\.\\-]([0-3]{0,1}[0-9]{1})' +
        '(?:' +
        '[\\/\\.\\-]' +
        '([0-9]{4}\s*\,?\s*|[0-9]{2}\s*\,?\s*)' +
        ')?' +
        '(\\W|$)', 'i');

    private OPENING_GROUP: number = 1;
    private WEEKDAY_GROUP: number = 2;
    private DAY_GROUP: number = 3;
    private MONTH_GROUP: number = 4;
    private YEAR_GROUP: number = 5;
    private ENDING_GROUP: number = 6;

    private TAG: string = 'DESlashDateFormatParser';

    private OTHER_PATTERNS: RegExp[] = [
        /^\d\.\d$/,
        /^\d\.\d{1,2}\.\d{1,2}$/
    ];

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        if (match[this.OPENING_GROUP] === '/' || match[this.ENDING_GROUP] === '/') {
            // Long skip, if there is some overlapping like:
            // XX[/YY/ZZ]
            // [XX/YY/]ZZ
            match.index += match[0].length;
            return null;
        }

        const index: number = match.index + match[this.OPENING_GROUP].length;
        const matchedText: string = match[0].substr(match[this.OPENING_GROUP].length, match[0].length - match[this.ENDING_GROUP].length);

        const result: ParsedResult = new ParsedResult({
            text: matchedText,
            index,
            ref,
        });

        if (matchedText.match(this.OTHER_PATTERNS[0]) || matchedText.match(this.OTHER_PATTERNS[1])) return null;

        // MM/dd -> OK
        // MM.dd -> NG
        if (!match[this.YEAR_GROUP] && match[0].indexOf('/') < 0) return null;

        let year: number = parseInt(match[this.YEAR_GROUP] || moment(ref).year() + '', 10);
        const month: number = parseInt(match[this.MONTH_GROUP], 10);
        const day: number = parseInt(match[this.DAY_GROUP], 10);

        if ((month < 1 || month > 12) || (day < 1 || day > 31)) return null;

        if (year < 100) {
            if (year > 50) {
                year = year + 1900;
            } else {
                year = year + 2000;
            }
        }

        result.start.assign(DAY, day);
        result.start.assign(MONTH, month);
        result.start.assign(YEAR, year);

        // Day of week
        if (match[this.WEEKDAY_GROUP]) {
            result.start.assign(WEEKDAY, WEEKDAY_OFFSET[match[this.WEEKDAY_GROUP].toLowerCase()]);
        }

        result.tags[this.TAG] = true;
        return result;
    }
}

import Parser from '../parser';
import {ParsedResult} from "../../result";
import {ParseOptions} from "../../chrono";
import {DAY, HOUR, MINUTE, MONTH, SECOND, UnitOfTime, WEEK, WEEKDAY, YEAR} from "../../constants";
import {INTEGER_WORDS_PATTERN, matchInteger, matchUnit} from '../../utils/DE';
import * as moment from "moment";
import {Moment} from "moment";

enum MODE {
    TIME,
    DATE,
    WEEK,
    NONE,
}

export default class DETimeAgoFormatParser extends Parser {
    private PATTERN: RegExp = new RegExp('' +
        '(\\W|^)vor\\s*' +
        '(' + INTEGER_WORDS_PATTERN + '|[0-9]+|einigen|eine[rm]\\s*halben|eine[rm])\\s*' +
        '(sekunden?|min(?:ute)?n?|stunden?|wochen?|tag(?:en)?|monat(?:en)?|jahr(?:en)?)\\s*' +
        '(?=(?:\\W|$))', 'i');

    private STRICT_PATTERN: RegExp = new RegExp('' +
        '(\\W|^)vor\\s*' +
        '([0-9]+|eine(?:r|m))\\s*' +
        '(sekunden?|minuten?|stunden?|tag(?:en)?)' +
        '(?=(?:\\W|$))', 'i');

    private TAG: string = 'DETimeAgoFormatParser';


    pattern(): RegExp {
        return this.isStrictMode() ? this.STRICT_PATTERN : this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        if (match.index > 0 && text[match.index - 1].match(/\w/)) return null;

        const matchedText: string = match[0].substr(match[1].length, match[0].length - match[1].length);
        const index: number = match.index + match[1].length;

        const result: ParsedResult = new ParsedResult({
            text: matchedText,
            index,
            ref
        });

        const matchedUnit: UnitOfTime | undefined = matchUnit(match[3].toLowerCase());

        if (matchedUnit) {
            const num: number = matchInteger(match[2].toLowerCase());
            const momentRef: Moment = moment(ref);
            let mode: MODE = MODE.NONE;
            switch (matchedUnit) {
                case HOUR:
                    momentRef.add(-num, 'hour');
                    mode = MODE.TIME;
                    break;
                case MINUTE:
                    momentRef.add(-num, 'minute');
                    mode = MODE.TIME;
                    break;
                case SECOND:
                    momentRef.add(-num, 'second');
                    mode = MODE.TIME;
                    break;
                case DAY:
                    momentRef.add(-num, 'd');
                    mode = MODE.DATE;
                    break;
                case MONTH:
                    momentRef.add(-num, 'month');
                    mode = MODE.DATE;
                    break;
                case YEAR:
                    momentRef.add(-num, 'year');
                    mode = MODE.DATE;
                    break;
                case WEEK:
                    momentRef.add(-num, 'week');
                    mode = MODE.WEEK;
                    break;
            }

            if (mode !== MODE.NONE) {
                result.tags[this.TAG] = true;
                switch (mode) {
                    case MODE.WEEK:
                        result.start.imply(DAY, momentRef.date());
                        result.start.imply(MONTH, momentRef.month() + 1);
                        result.start.imply(YEAR, momentRef.year());
                        result.start.imply(WEEKDAY, momentRef.day());
                        break;
                    case MODE.DATE:
                        result.start.assign(DAY, momentRef.date());
                        result.start.assign(MONTH, momentRef.month() + 1);
                        result.start.assign(YEAR, momentRef.year());
                        break;
                    case MODE.TIME:
                        result.start.imply(DAY, momentRef.date());
                        result.start.imply(MONTH, momentRef.month() + 1);
                        result.start.imply(YEAR, momentRef.year());
                        result.start.assign(HOUR, momentRef.hour());
                        result.start.assign(MINUTE, momentRef.minute());
                        result.start.assign(SECOND, momentRef.second());
                        break;
                }

                return result;
            }
        }

        return null;
    }

}

import Parser from '../parser';
import {ParsedComponents, ParsedResult} from "../../result";
import {ParseOptions} from "../../chrono";
import {DAY, HOUR, MERIDIEM, MINUTE, MONTH, SECOND, YEAR} from "../../constants";
import * as moment from "moment";
import {Moment} from "moment";

type Time = [number, number, number, number];

export default class DETimeExpressionParser extends Parser {
    private PATTERN: RegExp = new RegExp("(^|\\s|T)" +
        "(?:(?:um|von)\\s*)?" +
        "(\\d{1,4}|mittags?|mitternachts?)" +
        "(?:" +
        "(?:\\.|\\:|\\：)(\\d{1,2})" +
        "(?:" +
        "(?:\\:|\\：)(\\d{2})" +
        ")?" +
        ")?" +
        "(?:\\s*uhr)?" +
        "(?:\\s*(morgens|vormittags|mittags|nachmittags|abends|nachts))?" +
        "(?=\\W|$)", 'i');

    private END_PATTERN: RegExp = new RegExp("^\\s*" +
        "(\\-|\\–|\\~|\\〜|bis|\\?)\\s*" +
        "(\\d{1,4})" +
        "(?:" +
        "(?:\\.|\\:|\\：)(\\d{1,2})" +
        "(?:" +
        "(?:\\.|\\:|\\：)(\\d{1,2})" +
        ")?" +
        ")?" +
        "(?:\\s*(morgens|vormittags|mittags|nachmittags|abends|nachts))?" +
        "(?=\\W|$)", 'i');

    private HOUR_GROUP: number = 2;
    private MINUTE_GROUP: number = 3;
    private SECOND_GROUP: number = 4;
    private AM_PM_HOUR_GROUP: number = 5;

    private TAG: string = 'DETimeExpressionParser';

    private OTHER_PATTERNS: RegExp[] = [
        /mittags?/i,
        /mitternachts?/i,
        /^\s*([+\-])\s*\d{3,4}$/,
        /^\d+$/
    ];

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        // This pattern can be overlaped Ex. [12] AM, 1[2] AM
        if (match.index > 0 && text[match.index - 1].match(/\w/)) return null;

        const refMoment: Moment = moment(ref);

        const result: ParsedResult = new ParsedResult({
            text: match[0].substring(match[1].length),
            index: match.index + match[1].length,
            ref
        });

        result.tags[this.TAG] = true;

        result.start.imply(DAY, refMoment.date());
        result.start.imply(MONTH, refMoment.month() + 1);
        result.start.imply(YEAR, refMoment.year());

        if (!this.extractFirstChunk(result, match)) {
            return null;
        }

        if (!this.extractSecondChunk(text, result) && result.text.match(this.OTHER_PATTERNS[3])) {
            return null;
        }

        return result;
    }

    private extractFirstChunk(result: ParsedResult, match: RegExpExecArray): boolean {
        const time: Time | false = this.getTime(match);
        if (!time) {
            return false;
        }

        let [hour, minute, second, meridiem]: Time = time;

        if (second > -1) {
            result.start.assign(SECOND, second);
        }

        // ----- AM & PM
        if (match[this.AM_PM_HOUR_GROUP]) {
            if (hour > 12) return false;
            const ampm: string = match[this.AM_PM_HOUR_GROUP].toLowerCase();
            if (ampm === 'morgens' || ampm === 'vormittags') {
                meridiem = 0;
                if (hour === 12) hour = 0;
            } else {
                meridiem = 1;
                if (hour !== 12) hour += 12;
            }
        }

        result.start.assign(HOUR, hour);
        result.start.assign(MINUTE, minute);

        if (meridiem >= 0) {
            result.start.assign(MERIDIEM, meridiem);
        } else {
            if (hour < 12) {
                result.start.imply(MERIDIEM, 0);
            } else {
                result.start.imply(MERIDIEM, 1);
            }
        }

        return true;
    }

    private extractSecondChunk(text: string, result: ParsedResult): boolean {
        const match: RegExpExecArray | null = this.END_PATTERN.exec(text.substring(result.index + result.text.length));

        if (!match) {
            return false;
        }

        // Pattern "YY.YY -XXXX" is more like timezone offset
        if (match[0].match(this.OTHER_PATTERNS[2])) {
            return true;
        }

        const time: Time | false = this.getTime(match);
        if (!time) {
            return true;
        }

        let [hour, minute, second, meridiem]: Time = time;

        if (!result.end) {
            result.end = new ParsedComponents(undefined, result.start.date());
        }

        if (second > -1) {
            result.end.assign(SECOND, second);
        }

        // ----- AM & PM
        if (match[this.AM_PM_HOUR_GROUP]) {

            if (hour > 12) return false;

            const ampm: string = match[this.AM_PM_HOUR_GROUP].toLowerCase();
            if (ampm === 'morgens' || ampm === 'vormittags') {
                meridiem = 0;
                if (hour === 12) {
                    hour = 0;
                    if (!result.end.isCertain(DAY)) {
                        result.end.imply(DAY, result.end.get(DAY) + 1);
                    }
                }
            } else {
                meridiem = 1;
                if (hour !== 12) hour += 12;
            }

            if (!result.start.isCertain(MERIDIEM)) {
                if (meridiem === 0) {
                    result.start.imply(MERIDIEM, 0);

                    if (result.start.get(HOUR) === 12) {
                        result.start.assign(HOUR, 0);
                    }
                } else {
                    result.start.imply(MERIDIEM, 1);

                    if (result.start.get(HOUR) !== 12) {
                        result.start.assign(HOUR, result.start.get(HOUR) + 12);
                    }
                }
            }
        }

        result.text = result.text + match[0];
        result.end.assign(HOUR, hour);
        result.end.assign(MINUTE, minute);
        if (meridiem >= 0) {
            result.end.assign(MERIDIEM, meridiem);
        } else {
            const startAtPM: boolean = result.start.isCertain(MERIDIEM) && result.start.get(MERIDIEM) === 1;
            if (startAtPM && result.start.get(HOUR) > hour) {
                // 10pm - 1 (am)
                result.end.imply(MERIDIEM, 0);

            } else if (hour > 12) {
                result.end.imply(MERIDIEM, 1);
            }
        }

        if (result.end.date().getTime() < result.start.date().getTime()) {
            result.end.imply(DAY, result.end.get(DAY) + 1)
        }

        return true;
    }

    private getTime(match: RegExpExecArray): Time | false {
        let second: number = -1;
        let hour: number = 0;
        let minute: number = 0;
        let meridiem: number = -1;


        // ----- Second
        if (match[this.SECOND_GROUP]) {
            second = parseInt(match[this.SECOND_GROUP], 10);
            if (second >= 60) return false;
        }

        // ----- Hours
        if (this.OTHER_PATTERNS[0].test(match[this.HOUR_GROUP])) {
            meridiem = 1;
            hour = 12;
        } else if (this.OTHER_PATTERNS[1].test(match[this.HOUR_GROUP])) {
            meridiem = 0;
            hour = 0;
        } else {
            hour = parseInt(match[this.HOUR_GROUP], 10);
        }

        // ----- Minutes
        if (match[this.MINUTE_GROUP]) {
            minute = parseInt(match[this.MINUTE_GROUP], 10);
        } else if (hour > 100) {
            minute = hour % 100;
            hour = Math.floor(hour / 100);
        }

        if (minute >= 60) {
            return false;
        }

        if (hour > 24) {
            return false;
        }
        if (hour >= 12) {
            meridiem = 1;
        }

        return [hour, minute, second, meridiem];
    }
}

import Parser from '../parser';
import {ParsedResult} from "../../result";
import {ParseOptions} from "../../chrono";
import {WEEKDAY_OFFSET} from '../../utils/DE';
import * as moment from "moment";
import {Moment} from "moment";
import {DAY, MONTH, WEEKDAY, YEAR} from "../../constants";


export default class DEWeekdayParser extends Parser {
    private PATTERN: RegExp = new RegExp('(\\W|^)' +
        '(?:(?:\\,|\\(|\\（)\\s*)?' +
        '(?:a[mn]\\s*?)?' +
        '(?:(diese[mn]|letzte[mn]|n(?:ä|ae)chste[mn])\\s*)?' +
        '(' + Object.keys(WEEKDAY_OFFSET).join('|') + ')' +
        '(?:\\s*(?:\\,|\\)|\\）))?' +
        '(?:\\s*(diese|letzte|n(?:ä|ae)chste)\\s*woche)?' +
        '(?=\\W|$)', 'i');

    private PREFIX_GROUP: number = 2;
    private WEEKDAY_GROUP: number = 3;
    private POSTFIX_GROUP: number = 4;

    private OTHER_PATTERNS: RegExp[] = [
        /letzte/,
        /n(?:ä|ae)chste/,
        /diese/
    ];

    private TAG: string = 'DEWeekdayParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const matchedText: string = match[0].substr(match[1].length, match[0].length - match[1].length);
        const result: ParsedResult = new ParsedResult({
            text: matchedText,
            index,
            ref,
        });

        const offset: number | undefined = WEEKDAY_OFFSET[match[this.WEEKDAY_GROUP].toLowerCase()];
        if (offset === undefined) return null;

        const startMoment: Moment = moment(ref);
        const prefix: string = match[this.PREFIX_GROUP];
        const postfix: string = match[this.POSTFIX_GROUP];

        const refOffset: number = startMoment.day();
        const norm: string = (prefix || postfix || '').toLowerCase();
        if (this.OTHER_PATTERNS[0].test(norm)) {
            startMoment.day(offset - 7);
        } else if (this.OTHER_PATTERNS[1].test(norm)) {
            startMoment.day(offset + 7);
        } else if (this.OTHER_PATTERNS[2].test(norm)) {
            if (opt.forwardDate && refOffset > offset) {
                startMoment.day(offset + 7);
            } else {
                startMoment.day(offset);
            }
        } else {
            const calcOffset = offset - refOffset;
            const absOffset = Math.abs(calcOffset);
            if (opt.forwardDate && refOffset > offset) {
                startMoment.day(offset + 7);
            } else if (!opt.forwardDate && Math.abs(calcOffset - 7) < absOffset) {
                startMoment.day(offset - 7);
            } else if (!opt.forwardDate && Math.abs(calcOffset + 7) < absOffset) {
                startMoment.day(offset + 7);
            } else {
                startMoment.day(offset);
            }
        }

        result.tags[this.TAG] = true;
        result.start.assign(WEEKDAY, offset);
        result.start.imply(DAY, startMoment.date());
        result.start.imply(MONTH, startMoment.month() + 1);
        result.start.imply(YEAR, startMoment.year());
        return result;
    }
}

import * as moment from "moment";
import {Moment} from "moment";
import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {DAY, HOUR, MERIDIEM, MILLISECOND, MINUTE, MONTH, SECOND, YEAR} from "../../constants";

export default class ENCasualDateParser extends Parser {
    private PATTERN: RegExp = /(\W|^)(now|today|tonight|last\s*night|(?:tomorrow|tmr|yesterday)\s*|tomorrow|tmr|yesterday)(?=\W|$)/i;

    private OTHER_PATTERNS: RegExp[] = [
        /^tomorrow|^tmr/,
        /^yesterday/,
        /last\s*night/
    ];

    private TAG: string = 'ENCasualDateParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length),
            index,
            ref
        });

        const refMoment: Moment = moment(ref);
        const startMoment: Moment = refMoment.clone();
        const matchedText = result.text.toLowerCase();

        if(matchedText === 'tonight'){
            // Normally means this coming midnight
            result.start.imply(HOUR, 22);
            result.start.imply(MERIDIEM, 1);
        } else if (this.OTHER_PATTERNS[0].test(matchedText)) {
            // Check not "Tomorrow" on late night
            if(refMoment.hour() > 1) {
                startMoment.add(1, 'day');
            }
        } else if (this.OTHER_PATTERNS[1].test(matchedText)) {
            startMoment.add(-1, 'day');
        } else if(matchedText.match(this.OTHER_PATTERNS[2])) {
            result.start.imply(HOUR, 0);
            if (refMoment.hour() > 6) {
                startMoment.add(-1, 'day');
            }
        } else if (matchedText.match("now")) {
            result.start.assign(HOUR, refMoment.hour());
            result.start.assign(MINUTE, refMoment.minute());
            result.start.assign(SECOND, refMoment.second());
            result.start.assign(MILLISECOND, refMoment.millisecond());

        }

        result.start.assign(DAY, startMoment.date());
        result.start.assign(MONTH, startMoment.month() + 1);
        result.start.assign(YEAR, startMoment.year());
        result.tags[this.TAG] = true;
        return result;
    }

}

import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {HOUR} from "../../constants";

export default class ENCasualTimeParser extends Parser {
    private PATTERN: RegExp = /(\W|^)((this)?\s*(morning|afternoon|evening|noon))/i;

    private TIME_MATCH: number = 4;
    private TIME_MATCH_ALT: number = 3;

    private TAG: string = 'ENCasualTimeParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length),
            index,
            ref
        });

        result.tags[this.TAG] = true;

        const matchedText: string = (match[this.TIME_MATCH] || match[this.TIME_MATCH_ALT]).toLowerCase();

        if (matchedText == "afternoon") {
            result.start.imply(HOUR, opt.afternoon);
        } else if (matchedText == "evening") {
            result.start.imply(HOUR, opt.evening);
        } else if (matchedText == "morning") {
            result.start.imply(HOUR, opt.morning);
        } else if (matchedText == "noon") {
            result.start.imply(HOUR, opt.noon);
        }

        return result;
    }
}

import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {INTEGER_WORDS_PATTERN, matchInteger, matchUnit} from "../../utils/EN";
import * as moment from "moment";
import {UnitOfTime} from "../../constants";
import {deadlineCalculations} from "../../utils/general";

export default class ENDeadlineFormatParser extends Parser {
    private PATTERN: RegExp = new RegExp('(\\W|^)' +
        '(within|in)\\s*' +
        '(' + INTEGER_WORDS_PATTERN + '|[0-9]+|an?(?:\\s*few)?|half(?:\\s*an?)?)\\s*' +
        '(seconds?|min(?:ute)?s?|hours?|days?|weeks?|months?|years?)\\s*' +
        '(?=\\W|$)', 'i'
    );
    private STRICT_PATTERN: RegExp = new RegExp('(\\W|^)' +
        '(within|in)\\s*' +
        '(' + INTEGER_WORDS_PATTERN + '|[0-9]+|an?)\\s*' +
        '(seconds?|minutes?|hours?|days?)\\s*' +
        '(?=\\W|$)', 'i'
    );

    private NUM_MATCH: number = 3;
    private UNIT_MATCH: number = 4;

    private TAG: string = 'ENDeadlineFormatParser';

    pattern(): RegExp {
        return this.isStrictMode() ? this.STRICT_PATTERN : this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length, match[0].length - match[1].length),
            index,
            ref
        });

        result.tags[this.TAG] = true;

        const num: number = matchInteger(match[this.NUM_MATCH].toLowerCase());
        const matchedUnit: UnitOfTime | undefined = matchUnit(match[this.UNIT_MATCH].toLowerCase());

        if (num && matchedUnit && deadlineCalculations(num, matchedUnit, result, moment(ref))) {
            return result;
        }

        return null;
    }
}

/*
    ISO 8601
    http://www.w3.org/TR/NOTE-datetime
    - YYYY-MM-DD
    - YYYY-MM-DDThh:mmTZD
    - YYYY-MM-DDThh:mm:ssTZD
    - YYYY-MM-DDThh:mm:ss.sTZD 
    - TZD = (Z or +hh:mm or -hh:mm)
*/
import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {DAY, HOUR, MILLISECOND, MINUTE, MONTH, SECOND, TIMEZONE_OFFSET, YEAR} from "../../constants";

export default class ENISOFormatParser extends Parser {
    private PATTERN: RegExp = new RegExp('(\\W|^)'
        + '([0-9]{4})\\-([0-9]{1,2})\\-([0-9]{1,2})'
        + '(?:T' //..
        + '([0-9]{1,2}):([0-9]{1,2})' // hh:mm
        + '(?::([0-9]{1,2})(?:\\.(\\d{1,4}))?)?' // :ss.s
        + '(?:Z|([+-]\\d{2}):?(\\d{2})?)?' // TZD (Z or ±hh:mm or ±hhmm or ±hh)
        + ')?'  //..
        + '(?=\\W|$)', 'i');

    private YEAR_NUMBER_GROUP: number = 2;
    private MONTH_NUMBER_GROUP: number = 3;
    private DATE_NUMBER_GROUP: number = 4;
    private HOUR_NUMBER_GROUP: number = 5;
    private MINUTE_NUMBER_GROUP: number = 6;
    private SECOND_NUMBER_GROUP: number = 7;
    private MILLISECOND_NUMBER_GROUP: number = 8;
    private TZD_HOUR_OFFSET_GROUP: number = 9;
    private TZD_MINUTE_OFFSET_GROUP: number = 10;

    private TAG: string = 'ENISOFormatParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length),
            index,
            ref
        });

        result.tags[this.TAG] = true;

        result.start.assign(YEAR, parseInt(match[this.YEAR_NUMBER_GROUP], 10));
        result.start.assign(MONTH, parseInt(match[this.MONTH_NUMBER_GROUP], 10));
        result.start.assign(DAY, parseInt(match[this.DATE_NUMBER_GROUP], 10));

        if (result.start.get(MONTH) > 12 || result.start.get(MONTH) < 1 ||
            result.start.get(DAY) > 31 || result.start.get(DAY) < 1) {
            return null;
        }

        if (match[this.HOUR_NUMBER_GROUP] != null) {
            result.start.assign(HOUR, parseInt(match[this.HOUR_NUMBER_GROUP], 10));
            result.start.assign(MINUTE, parseInt(match[this.MINUTE_NUMBER_GROUP], 10));

            if (match[this.SECOND_NUMBER_GROUP] != null) {
                result.start.assign(SECOND, parseInt(match[this.SECOND_NUMBER_GROUP],10));
            }

            if (match[this.MILLISECOND_NUMBER_GROUP] != null) {
                result.start.assign(MILLISECOND, parseInt(match[this.MILLISECOND_NUMBER_GROUP], 10));
            }

            if (match[this.TZD_HOUR_OFFSET_GROUP] == null) {
                result.start.assign(TIMEZONE_OFFSET, 0);
            } else {
                let minuteOffset: number = 0;
                const hourOffset: number = parseInt(match[this.TZD_HOUR_OFFSET_GROUP], 10);
                if (match[this.TZD_MINUTE_OFFSET_GROUP] != null)
                    minuteOffset = parseInt(match[this.TZD_MINUTE_OFFSET_GROUP], 10);

                let offset: number = hourOffset * 60;
                if (offset < 0) {
                    offset -= minuteOffset;
                } else {
                    offset += minuteOffset;
                }

                result.start.assign(TIMEZONE_OFFSET, offset);
            }
        }

        return result;
    }
}

import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {MONTH_OFFSET, ORDINAL_WORDS, ORDINAL_WORDS_PATTERN, WEEKDAY_OFFSET, yearCalculation} from "../../utils/EN";
import {DAY, MONTH, WEEKDAY, YEAR} from "../../constants";
import {getAppropriateYear} from "../../utils/general";

export default class ENMonthNameLittleEndianParser extends Parser {
    private PATTERN: RegExp = new RegExp('(\\W|^)' +
        '(?:on\\s*?)?' +
        '(?:(Sunday|Monday|Tuesday|Wednesday|Thursday|Friday|Saturday|Sun|Mon|Tue|Wed|Thu|Fri|Sat)\\s*,?\\s*)?' +
        '(([0-9]{1,2})(?:st|nd|rd|th)?|' + ORDINAL_WORDS_PATTERN + ')' +
        '(?:\\s*' +
        '(?:to|\\-|\\–|until|through|till|\\s)\\s*' +
        '(([0-9]{1,2})(?:st|nd|rd|th)?|' + ORDINAL_WORDS_PATTERN + ')' +
        ')?' +
        '(?:-|\/|\\s*(?:of)?\\s*)' +
        '(Jan(?:uary|\\.)?|Feb(?:ruary|\\.)?|Mar(?:ch|\\.)?|Apr(?:il|\\.)?|May|Jun(?:e|\\.)?|Jul(?:y|\\.)?|Aug(?:ust|\\.)?|Sep(?:tember|\\.)?|Oct(?:ober|\\.)?|Nov(?:ember|\\.)?|Dec(?:ember|\\.)?)' +
        '(?:' +
        '(?:-|\/|,?\\s*)' +
        '((?:' +
        '[1-9][0-9]{0,3}\\s*(?:BE|AD|BC)|' +
        '[1-2][0-9]{3}' +
        ')(?![^\\s]\\d))' +
        ')?' +
        '(?=\\W|$)', 'i'
    );

    private WEEKDAY_GROUP: number = 2;
    private DATE_GROUP: number = 3;
    private DATE_NUM_GROUP: number = 4;
    private DATE_TO_GROUP: number = 5;
    private DATE_TO_NUM_GROUP: number = 6;
    private MONTH_NAME_GROUP: number = 7;
    private YEAR_GROUP: number = 8;

    private TAG: string = 'ENMonthNameLittleEndianParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length, match[0].length - match[1].length),
            index,
            ref
        });

        result.tags[this.TAG] = true;

        const month: number = MONTH_OFFSET[match[this.MONTH_NAME_GROUP].toLowerCase()];

        if (!(month || month === 0)) {
            return null;
        }

        const day: number = match[this.DATE_NUM_GROUP] ?
            parseInt(match[this.DATE_NUM_GROUP], 10) :
            ORDINAL_WORDS[match[this.DATE_GROUP].trim().replace('-', ' ').toLowerCase()];


        if (!day) {
            return null;
        }

        result.start.assign(DAY, day);
        result.start.assign(MONTH, month);

        let year: number | null = yearCalculation(match[this.YEAR_GROUP]);


        if (year) {
            result.start.assign(YEAR, year);
        } else {
            getAppropriateYear(result.start, ref);
        }

        // Weekday component
        if (match[this.WEEKDAY_GROUP]) {
            const weekday: number = WEEKDAY_OFFSET[match[this.WEEKDAY_GROUP].toLowerCase()];
            if (weekday || weekday === 0) {
                result.start.assign(WEEKDAY, weekday);
            }
        }

        // Text can be 'range' value. Such as '12 - 13 January 2012'
        if (match[this.DATE_TO_GROUP]) {
            const endDate = match[this.DATE_TO_NUM_GROUP] ?
                parseInt(match[this.DATE_TO_NUM_GROUP], 10) :
                ORDINAL_WORDS[match[this.DATE_TO_GROUP].trim().replace('-', ' ').toLowerCase()];

            if (endDate) {
                result.end = result.start.clone();
                result.end.assign(DAY, endDate);
            }
        }

        return result;
    }
}

/*

    The parser for parsing US's date format that begin with month's name.

    EX.
        - January 13
        - January 13, 2012
        - January 13 - 15, 2012
        - Tuesday, January 13, 2012

    Watch out for:
        - January 12:00
        - January 12.44
        - January 1222344
*/
import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {MONTH_OFFSET, ORDINAL_WORDS, ORDINAL_WORDS_PATTERN, WEEKDAY_OFFSET, yearCalculation} from "../../utils/EN";
import {DAY, MONTH, WEEKDAY, YEAR} from "../../constants";
import {getAppropriateYear} from "../../utils/general";

export default class ENMonthNameMiddleEndianParser extends Parser {
    private PATTERN: RegExp = new RegExp('(\\W|^)' +
        '(?:' +
        '(?:on\\s*?)?' +
        '(Sunday|Monday|Tuesday|Wednesday|Thursday|Friday|Saturday|Sun\\.?|Mon\\.?|Tue\\.?|Wed\\.?|Thu\\.?|Fri\\.?|Sat\\.?)' +
        '\\s*,?\\s*)?' +
        '(Jan\\.?|January|Feb\\.?|February|Mar\\.?|March|Apr\\.?|April|May\\.?|Jun\\.?|June|Jul\\.?|July|Aug\\.?|August|Sep\\.?|Sept\\.?|September|Oct\\.?|October|Nov\\.?|November|Dec\\.?|December)' +
        '(?:-|\/|\\s*,?\\s*)' +
        '(([0-9]{1,2})(?:st|nd|rd|th)?|' + ORDINAL_WORDS_PATTERN + ')(?!\\s*(?:am|pm))\\s*' + '' +
        '(?:' +
        '(?:to|\\-)\\s*' +
        '(([0-9]{1,2})(?:st|nd|rd|th)?| ' + ORDINAL_WORDS_PATTERN + ')\\s*' +
        ')?' +
        '(?:' +
        '(?:-|\/|\\s*,?\\s*)' +
        '(?:([0-9]{4})\\s*(BE|AD|BC)?|([0-9]{1,4})\\s*(AD|BC))\\s*' +
        ')?' +
        '(?=\\W|$)(?!\\:\\d)', 'i');

    private WEEKDAY_GROUP: number = 2;
    private MONTH_NAME_GROUP: number = 3;
    private DATE_GROUP: number = 4;
    private DATE_NUM_GROUP: number = 5;
    private DATE_TO_GROUP: number = 6;
    private DATE_TO_NUM_GROUP: number = 7;
    private YEAR_GROUP: number = 8;
    private YEAR_BE_GROUP: number = 9;
    private YEAR_GROUP2: number = 10;
    private YEAR_BE_GROUP2: number = 11;

    private TAG: string = 'ENMonthNameMiddleEndianParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length, match[0].length - match[1].length),
            index,
            ref
        });

        result.tags[this.TAG] = true;

        const month: number = MONTH_OFFSET[match[this.MONTH_NAME_GROUP].toLowerCase()];

        if (!(month || month === 0)) {
            return null;
        }

        const day: number = match[this.DATE_NUM_GROUP] ?
            parseInt(match[this.DATE_NUM_GROUP], 10) :
            ORDINAL_WORDS[match[this.DATE_GROUP].trim().replace('-', ' ').toLowerCase()];


        if (!day) {
            return null;
        }

        result.start.assign(DAY, day);
        result.start.assign(MONTH, month);

        let year: number | null = yearCalculation(
            match[this.YEAR_GROUP] || match[this.YEAR_GROUP2],
            match[this.YEAR_BE_GROUP] || match[this.YEAR_BE_GROUP2]
        );

        if (year) {
            result.start.assign(YEAR, year);
        } else {
            getAppropriateYear(result.start, ref);
        }

        // Weekday component
        if (match[this.WEEKDAY_GROUP]) {
            const weekday: number = WEEKDAY_OFFSET[match[this.WEEKDAY_GROUP].toLowerCase()];
            if (weekday || weekday === 0) {
                result.start.assign(WEEKDAY, weekday);
            }
        }

        // Text can be 'range' value. Such as '12 - 13 January 2012'
        if (match[this.DATE_TO_GROUP]) {
            const endDate = match[this.DATE_TO_NUM_GROUP] ?
                parseInt(match[this.DATE_TO_NUM_GROUP], 10) :
                ORDINAL_WORDS[match[this.DATE_TO_GROUP].trim().replace('-', ' ').toLowerCase()];

            if (endDate) {
                result.end = result.start.clone();
                result.end.assign(DAY, endDate);
            }
        }

        return result;
    }
}

/*

    The parser for parsing month name and year.

    EX. 
        - January
        - January 2012
        - January, 2012
*/
import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {MONTH_OFFSET, yearCalculation} from "../../utils/EN";
import {DAY, MONTH, YEAR} from "../../constants";
import {getAppropriateYear} from "../../utils/general";

export default class ENMonthNameParser extends Parser {
    private PATTERN: RegExp = new RegExp('(^|\\D\\s+|[^\\w\\s])' +
        '(Jan\\.?|January|Feb\\.?|February|Mar\\.?|March|Apr\\.?|April|May\\.?|Jun\\.?|June|Jul\\.?|July|Aug\\.?|August|Sep\\.?|Sept\\.?|September|Oct\\.?|October|Nov\\.?|November|Dec\\.?|December)' +
        '\\s*' +
        '(?:' +
        '[,-]?\\s*([0-9]{4})(\\s*BE|AD|BC)?' +
        ')?' +
        '(?=[^\\s\\w]|\\s+[^0-9]|\\s+$|$)', 'i');

    private MONTH_NAME_GROUP: number = 2;
    private YEAR_GROUP: number = 3;
    private YEAR_BE_GROUP: number = 4;

    private TAG: string = 'ENMonthNameParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length, match[0].length - match[1].length),
            index,
            ref
        });

        result.tags[this.TAG] = true;

        const month: number = MONTH_OFFSET[match[this.MONTH_NAME_GROUP].toLowerCase()];

        if (!(month || month === 0)) {
            return null;
        }

        result.start.assign(DAY, 1);
        result.start.assign(MONTH, month);

        let year: number | null = yearCalculation(
            match[this.YEAR_GROUP],
            match[this.YEAR_BE_GROUP]
        );

        if (year) {
            result.start.assign(YEAR, year);
        } else {
            getAppropriateYear(result.start, ref);
        }


        return result;
    }
}

import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {INTEGER_WORDS_PATTERN, matchInteger, matchUnit} from "../../utils/EN";
import * as moment from "moment";
import {Moment} from "moment";
import {DAY, HOUR, MINUTE, MONTH, SECOND, UnitOfTime, WEEK, YEAR} from "../../constants";

export default class ENRelativeDateFormatParser extends Parser {
    private PATTERN: RegExp = new RegExp('(\\W|^)' +
        '(this|next|last|past)\\s*' +
        '(' + INTEGER_WORDS_PATTERN + '|[0-9]+|few|half(?:\\s*an?)?)?\\s*' +
        '(seconds?|min(?:ute)?s?|hours?|days?|weeks?|months?|years?)(?=\\s*)' +
        '(?=\\W|$)', 'i'
    );

    private MODIFIER_WORD_GROUP: number = 2;
    private MULTIPLIER_WORD_GROUP: number = 3;
    private RELATIVE_WORD_GROUP: number = 4;

    private OTHER_PATTERNS: RegExp[] = [
        /^next/,
        /^this/
    ];

    private TAG: string = 'ENRelativeDateFormatParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length, match[0].length - match[1].length),
            index,
            ref
        });

        result.tags[this.TAG] = true;

        const matchedUnit: string = match[this.RELATIVE_WORD_GROUP].toLowerCase();
        const unit: UnitOfTime | undefined = matchUnit(matchedUnit);

        if (!unit) {
            return null;
        }

        const matchedMultiplier: string = match[this.MULTIPLIER_WORD_GROUP];
        const matchedModifier: string = match[this.MODIFIER_WORD_GROUP].toLowerCase();
        const momentRef: Moment = moment(ref);
        const num: number = matchInteger(matchedMultiplier);

        if (matchedModifier.match(this.OTHER_PATTERNS[1])) {
            if (matchedMultiplier) {
                return null;
            }

            switch (unit) {
                case WEEK:
                    momentRef.set('weekday', 0);
                    result.start.imply(DAY, momentRef.date());
                    result.start.imply(MONTH, momentRef.month() + 1);
                    result.start.imply(YEAR, momentRef.year());
                    break;
                case MONTH:
                    momentRef.set('date', 1);
                    result.start.imply(DAY, momentRef.date());
                    result.start.assign(MONTH, momentRef.month() + 1);
                    result.start.assign(YEAR, momentRef.year());
                    break;
                case YEAR:
                    momentRef.set('dayOfYear', 1);
                    result.start.imply(DAY, momentRef.date());
                    result.start.imply(MONTH, momentRef.month() + 1);
                    result.start.assign(YEAR, momentRef.year());
                    break;

            }
        } else {
            const modifier: number = matchedModifier.match(this.OTHER_PATTERNS[0]) ? 1 : -1;
            const offset: number = modifier * (num || 1);
            let timeMode: boolean = false;

            switch (unit) {
                case SECOND:
                    timeMode = true;
                    momentRef.add(offset, 'second');
                    result.start.imply(MINUTE, momentRef.minute());
                    result.start.imply(SECOND, momentRef.second());
                    break;
                case MINUTE:
                    timeMode = true;
                    momentRef.add(offset, 'minute');
                    result.start.assign(MINUTE, momentRef.minute());
                    result.start.imply(SECOND, momentRef.second());
                    break;
                case HOUR:
                    timeMode = true;
                    momentRef.add(offset, 'hour');
                    result.start.imply(MINUTE, momentRef.minute());
                    result.start.imply(SECOND, momentRef.second());
                    break;
                case DAY:
                    momentRef.add(offset, 'd');
                    result.start.assign(YEAR, momentRef.year());
                    result.start.assign(MONTH, momentRef.month() + 1);
                    result.start.assign(DAY, momentRef.date());
                    break;
                case WEEK:
                    momentRef.add(offset * 7, 'd');
                    result.start.imply(MONTH, momentRef.month() + 1);
                    result.start.imply(YEAR, momentRef.year());
                    result.start.imply(DAY, momentRef.date());
                    break;
                case MONTH:
                    momentRef.add(offset, 'month');
                    momentRef.set('date', 1);
                    result.start.assign(YEAR, momentRef.year());
                    result.start.assign(MONTH, momentRef.month() + 1);
                    result.start.imply(DAY, momentRef.date());
                    break;
                case YEAR:
                    momentRef.add(offset, 'year');
                    momentRef.set('dayOfYear', 1);
                    result.start.imply(DAY, momentRef.date());
                    result.start.imply(MONTH, momentRef.month() + 1);
                    result.start.assign(YEAR, momentRef.year());
                    break;
                default:
                    return null;
            }

            if (timeMode) {
                result.start.assign(HOUR, momentRef.hour());
                result.start.assign(YEAR, momentRef.year());
                result.start.assign(MONTH, momentRef.month() + 1);
                result.start.assign(DAY, momentRef.date());
            }
        }


        return result;
    }
}

/*
    Date format with slash "/" (also "-" and ".") between numbers
    - Tuesday 11/3/2015 
    - 11/3/2015
    - 11/3

    By default the paser us "middle-endien" format (US English),
    then fallback to little-endian if failed.
    - 11/3/2015 = November 3rd, 2015
    - 23/4/2015 = April 23th, 2015

    If "littleEndian" config is set, the parser will try the little-endian first. 
    - 11/3/2015 = March 11th, 2015
*/

import * as moment from 'moment';
import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {Config} from "../../options";
import {DAY, MONTH, WEEKDAY, YEAR} from "../../constants";
import {WEEKDAY_OFFSET} from "../../utils/EN";
import {checkMonthDaysValid} from "../../utils/general";

export default class ENSlashDateFormatParser extends Parser {
    private PATTERN: RegExp = new RegExp('(\\W|^)' +
        '(?:' +
        '(?:on\\s*?)?' +
        '((?:sun|mon|tues?|wed(?:nes)?|thu(?:rs?)?|fri|sat(?:ur)?)(?:day)?)' +
        '\\s*\\,?\\s*' +
        ')?' +
        '([0-3]{0,1}[0-9]{1})[\\/\\.\\-]([0-3]{0,1}[0-9]{1})' +
        '(?:' +
        '[\\/\\.\\-]' +
        '([0-9]{4}\s*\,?\s*|[0-9]{2}\s*\,?\s*)' +
        ')?' +
        '(\\W|$)', 'i');

    private OPENING_GROUP: number = 1;
    private ENDING_GROUP: number = 6;

    private WEEKDAY_GROUP: number = 2;

    private FIRST_NUMBERS_GROUP: number = 3;
    private SECOND_NUMBERS_GROUP: number = 4;

    private YEAR_GROUP: number = 5;

    private TAG: string = 'ENSlashDateFormatParser';

    private MONTH_GROUP: number;
    private DAY_GROUP: number;

    private OTHER_PATTERNS: RegExp[] = [
        /^\d\.\d$/,
        /^\d\.\d{1,2}\.\d{1,2}$/
    ];

    constructor(config: Config = {}) {
        super(config);
        if (config.littleEndian) {
            this.MONTH_GROUP = this.SECOND_NUMBERS_GROUP;
            this.DAY_GROUP = this.FIRST_NUMBERS_GROUP;
        } else {
            this.DAY_GROUP = this.SECOND_NUMBERS_GROUP;
            this.MONTH_GROUP = this.FIRST_NUMBERS_GROUP;
        }
    }

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        if (match[this.OPENING_GROUP] == '/' || match[this.ENDING_GROUP] == '/') {
            // Long skip, if there is some overlapping like:
            // XX[/YY/ZZ]
            // [XX/YY/]ZZ
            match.index += match[0].length;
            return null;
        }

        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[this.OPENING_GROUP].length, match[0].length - match[this.ENDING_GROUP].length),
            index,
            ref
        });

        result.tags[this.TAG] = true;
        const matchedText: string = result.text.toLowerCase();

        if (matchedText.match(this.OTHER_PATTERNS[0]) || matchedText.match(this.OTHER_PATTERNS[1])) {
            return null;
        }

        // MM/dd -> OK
        // MM.dd -> NG
        if (!match[this.YEAR_GROUP] && match[0].indexOf('/') < 0) {
            return null;
        }

        let year: number = match[this.YEAR_GROUP] ? parseInt(match[this.YEAR_GROUP], 10) : moment(ref).year();
        let month: number = parseInt(match[this.MONTH_GROUP], 10);
        let day: number = parseInt(match[this.DAY_GROUP], 10);

        if (month < 1 || day < 1 || day > 31) {
            return null
        }

        if (year < 100) {
            if (year > 50) {
                year = year + 1900;
            } else {
                year = year + 2000;
            }
        }

        if (month > 12) {
            if (month <= 31 && day <= 12 && checkMonthDaysValid(month, day, year)) {
                [month, day] = [day, month];
            } else {
                return null;
            }
        }

        result.start.assign(DAY, day);
        result.start.assign(MONTH, month);
        if (match[this.YEAR_GROUP]) {
            result.start.assign(YEAR, year);
        } else {
            result.start.imply(YEAR, year);
        }

        if (match[this.WEEKDAY_GROUP]) {
            result.start.assign(WEEKDAY, WEEKDAY_OFFSET[match[this.WEEKDAY_GROUP].toLowerCase()]);
        }

        return result;
    }
}

/*
    Date format with slash "/" between numbers like ENSlashDateFormatParser,
    but this parser expect year before month and date. 
    - YYYY/MM/DD
    - YYYY-MM-DD
    - YYYY.MM.DD
*/
import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {checkMonthDaysValid} from "../../utils/general";
import {DAY, MONTH, YEAR} from "../../constants";

export default class ENSlashDateFormatStartWithYearParser extends Parser {
    private PATTERN: RegExp = new RegExp('(\\W|^)'
        + '([0-9]{4})[\\-\\.\\/]([0-9]{1,2})[\\-\\.\\/]([0-9]{1,2})'
        + '(?=\\W|$)', 'i');

    private YEAR_NUMBER_GROUP: number = 2;
    private MONTH_NUMBER_GROUP: number = 3;
    private DATE_NUMBER_GROUP: number = 4;

    private TAG: string = 'ENSlashDateFormatStartWithYearParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length),
            index,
            ref
        });

        result.tags[this.TAG] = true;

        const year: number = parseInt(match[this.YEAR_NUMBER_GROUP], 10);
        const month: number = parseInt(match[this.MONTH_NUMBER_GROUP], 10);
        const day: number = parseInt(match[this.DATE_NUMBER_GROUP], 10);

        if (month > 12 || month < 1 || day > 31 || day < 1 || !checkMonthDaysValid(day, month, year)) {
            return null;
        }

        result.start.assign(YEAR, year);
        result.start.assign(MONTH, month);
        result.start.assign(DAY, day);

        return result;
    }
}

/*
    Month/Year date format with slash "/" (also "-" and ".") between numbers 
    - 11/05
    - 06/2005
*/

import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {DAY, HOUR, MONTH, YEAR} from "../../constants";

export default class ENSlashMonthFormatParser extends Parser {
    private PATTERN: RegExp = new RegExp('(^|[^\\d/]\\s+|[^\\w\\s])' +
        '([0-9]|0[1-9]|1[012])/([0-9]{4})' +
        '([^\\d/]|$)', 'i');

    private OPENING_GROUP: number = 1;
    private ENDING_GROUP: number = 4;
    private MONTH_GROUP: number = 2;
    private YEAR_GROUP: number = 3;

    private TAG: string = 'ENSlashMonthFormatParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[this.OPENING_GROUP].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[this.OPENING_GROUP].length, match[0].length - (1 + match[this.ENDING_GROUP].length)).trim(),
            index,
            ref
        });

        result.tags[this.TAG] = true;

        result.start.imply(DAY, 1);
        result.start.assign(MONTH, parseInt(match[this.MONTH_GROUP], 10));
        result.start.assign(YEAR, parseInt(match[this.YEAR_GROUP], 10));


        return result;
    }
}

import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {DAY, HOUR, MINUTE, MONTH, SECOND, WEEKDAY, YEAR} from "../../constants";
import {
    extractDateTimeUnitFragments,
    FragmentMap,
    FragmentName,
    TIME_UNIT_PATTERN,
    TIME_UNIT_STRICT_PATTERN
} from "../../utils/EN";
import * as moment from "moment";
import {Moment} from "moment";

export default class ENTimeAgoFormatParser extends Parser {
    private PATTERN: RegExp = new RegExp('' +
        '(\\W|^)' +
        '(?:within\\s*)?' +
        '(' + TIME_UNIT_PATTERN + ')' +
        '(?:ago|before|earlier)(?=(?:\\W|$))', 'i');

    private STRICT_PATTERN: RegExp = new RegExp('' +
        '(\\W|^)' +
        '(?:within\\s*)?' +
        '(' + TIME_UNIT_STRICT_PATTERN + ')' +
        'ago(?=(?:\\W|$))', 'i');

    private TAG: string = 'ENTimeAgoFormatParser';

    private OTHER_PATTERNS: RegExp[] = [
        /\w/
    ];

    pattern(): RegExp {
        return this.isStrictMode() ? this.STRICT_PATTERN : this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        if (match.index > 0 && text[match.index - 1].match(this.OTHER_PATTERNS[0])) return null;

        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length, match[0].length - match[1].length),
            index,
            ref
        });

        result.tags[this.TAG] = true;

        const fragments: FragmentMap = extractDateTimeUnitFragments(match[2]);
        const momentRef: Moment = moment(ref);

        (Object.keys(fragments) as FragmentName[]).forEach((fragment: FragmentName) => {
            momentRef.add(-fragments[fragment]!, fragment);
        });

        if ((fragments['hour'] || 0) > 0 || (fragments['minute'] || 0) > 0 || (fragments['second'] || 0) > 0) {
            result.start.assign(HOUR, momentRef.hour());
            result.start.assign(MINUTE, momentRef.minute());
            result.start.assign(SECOND, momentRef.second());
        }

        if ((fragments['d'] || 0) > 0 || (fragments['month'] || 0) > 0 || (fragments['year'] || 0) > 0) {
            result.start.assign(DAY, momentRef.date());
            result.start.assign(MONTH, momentRef.month() + 1);
            result.start.assign(YEAR, momentRef.year());
        } else {
            if ((fragments['week'] || 0) > 0) {
                result.start.imply(WEEKDAY, momentRef.day());
            }

            result.start.imply(DAY, momentRef.date());
            result.start.imply(MONTH, momentRef.month() + 1);
            result.start.imply(YEAR, momentRef.year());
        }

        return result;
    }
}

import Parser from '../parser';
import {ParsedComponents, ParsedResult} from "../../result";
import {ParseOptions} from "../../chrono";
import {DAY, HOUR, MERIDIEM, MILLISECOND, MINUTE, MONTH, SECOND, YEAR} from "../../constants";
import * as moment from "moment";
import {Moment} from "moment";

type Time = [number, number, number, number, number];

export default class ENTimeExpressionParser extends Parser {
    private PATTERN: RegExp = new RegExp("(^|\\s|T)" +
        "(?:(?:at|from)\\s*)??" +
        "(\\d{1,4}|noon|midnight)" +
        "(?:" +
        "(?:\\.|\\:|\\：)(\\d{1,2})" +
        "(?:" +
        "(?:\\:|\\：)(\\d{2})(?:\\.(\\d{1,6}))?" +
        ")?" +
        ")?" +
        "(?:\\s*(A\\.M\\.|P\\.M\\.|AM?|PM?|O\\W*CLOCK))?" +
        "(?=\\W|$)", 'i');

    private END_PATTERN: RegExp = new RegExp("^\\s*" +
        "(\\-|\\–|\\~|\\〜|to|(?:un)?till?|\\?)\\s*" +
        "(\\d{1,4})" +
        "(?:" +
        "(?:\\.|\\:|\\：)(\\d{1,2})" +
        "(?:" +
        "(?:\\.|\\:|\\：)(\\d{1,2})(?:\\.(\\d{1,6}))?" +
        ")?" +
        ")?" +
        "(?:\\s*(A\\.M\\.|P\\.M\\.|AM?|PM?|O\\W*CLOCK))?" +
        "(?=\\W|$)", 'i');

    private HOUR_GROUP = 2;
    private MINUTE_GROUP = 3;
    private SECOND_GROUP = 4;
    private MILLI_SECOND_GROUP = 5;
    private AM_PM_HOUR_GROUP = 6;

    private TAG: string = 'ENTimeExpressionParser';

    private OTHER_PATTERNS: RegExp[] = [
        /\w/,
        /^\s*([+\-])\d{3,4}$/,
        /^\d+$/,

    ];

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        // This pattern can be overlaped Ex. [12] AM, 1[2] AM
        if (match.index > 0 && text[match.index - 1].match(this.OTHER_PATTERNS[0])) return null;

        const refMoment: Moment = moment(ref);

        const result: ParsedResult = new ParsedResult({
            text: match[0].substring(match[1].length),
            index: match.index + match[1].length,
            ref
        });

        result.tags[this.TAG] = true;

        result.start.imply(DAY, refMoment.date());
        result.start.imply(MONTH, refMoment.month() + 1);
        result.start.imply(YEAR, refMoment.year());

        if (!this.extractFirstChunk(result, match)) {
            return null;
        }

        if (!this.extractSecondChunk(text, result) && result.text.match(this.OTHER_PATTERNS[2])) {
            return null;
        }

        return result;
    }

    private extractFirstChunk(result: ParsedResult, match: RegExpExecArray): boolean {
        const time: Time | false = this.getTime(match);
        if (!time) {
            return false;
        }

        let [hour, minute, second, millisecond, meridiem]: Time = time;

        if (second > -1) {
            result.start.assign(SECOND, second);
        }

        if (millisecond > -1) {
            result.start.assign(MILLISECOND, millisecond);
        }

        // ----- AM & PM
        if (match[this.AM_PM_HOUR_GROUP]) {
            if (hour > 12) return false;
            const ampm: string = match[this.AM_PM_HOUR_GROUP][0].toLowerCase();
            if (ampm === 'a') {
                meridiem = 0;
                if (hour === 12) hour = 0;
            } else if (ampm === 'p') {
                meridiem = 1;
                if (hour !== 12) hour += 12;
            }
        }

        result.start.assign(HOUR, hour);
        result.start.assign(MINUTE, minute);

        if (meridiem >= 0) {
            result.start.assign(MERIDIEM, meridiem);
        } else {
            if (hour < 12) {
                result.start.imply(MERIDIEM, 0);
            } else {
                result.start.imply(MERIDIEM, 1);
            }
        }

        return true;
    }

    private extractSecondChunk(text: string, result: ParsedResult): boolean {
        const match: RegExpExecArray | null = this.END_PATTERN.exec(text.substring(result.index + result.text.length));

        if (!match) {
            return false;
        }

        // Pattern "YY.YY -XXXX" is more like timezone offset
        if (match[0].match(this.OTHER_PATTERNS[1])) {
            return true;
        }

        const time: Time | false = this.getTime(match);
        if (!time) {
            return true;
        }

        let [hour, minute, second, millisecond, meridiem]: Time = time;

        if (!result.end) {
            result.end = new ParsedComponents(undefined, result.start.date());
        }

        if (second > -1) {
            result.end.assign(SECOND, second);
        }

        if (millisecond > -1) {
            result.end.assign(MILLISECOND, millisecond);
        }

        // ----- AM & PM
        if (match[this.AM_PM_HOUR_GROUP]) {

            if (hour > 12) return false;

            const ampm: string = match[this.AM_PM_HOUR_GROUP][0].toLowerCase();
            if (ampm === 'a') {
                meridiem = 0;
                if (hour === 12) {
                    hour = 0;
                    if (!result.end.isCertain(DAY)) {
                        result.end.imply(DAY, result.end.get(DAY) + 1);
                    }
                }
            }

            if (ampm === 'p') {
                meridiem = 1;
                if (hour !== 12) hour += 12;
            }

            if (!result.start.isCertain(MERIDIEM)) {
                if (meridiem === 0) {
                    result.start.imply(MERIDIEM, 0);

                    if (result.start.get(HOUR) === 12) {
                        result.start.assign(HOUR, 0);
                    }
                } else {
                    result.start.imply(MERIDIEM, 1);

                    if (result.start.get(HOUR) !== 12) {
                        result.start.assign(HOUR, result.start.get(HOUR) + 12);
                    }
                }
            }
        }

        result.text = result.text + match[0];
        result.end.assign(HOUR, hour);
        result.end.assign(MINUTE, minute);
        if (meridiem >= 0) {
            result.end.assign(MERIDIEM, meridiem);
        } else {
            const startAtPM: boolean = result.start.isCertain(MERIDIEM) && result.start.get(MERIDIEM) === 1;
            if (startAtPM && result.start.get(HOUR) > hour) {
                // 10pm - 1 (am)
                result.end.imply(MERIDIEM, 0);

            } else if (hour > 12) {
                result.end.imply(MERIDIEM, 1);
            }
        }

        if (result.end.date().getTime() < result.start.date().getTime()) {
            result.end.imply(DAY, result.end.get(DAY) + 1)
        }

        return true;
    }

    private getTime(match: RegExpExecArray): Time | false {
        let millisecond: number = -1;
        let second: number = -1;
        let hour: number = 0;
        let minute: number = 0;
        let meridiem: number = -1;


        // ----- Millisecond
        if (match[this.MILLI_SECOND_GROUP]) {
            millisecond = parseInt(match[this.MILLI_SECOND_GROUP].substr(0, 3), 10);
            if (millisecond >= 1000) return false;
        }

        // ----- Second
        if (match[this.SECOND_GROUP]) {
            second = parseInt(match[this.SECOND_GROUP], 10);
            if (second >= 60) return false;
        }

        // ----- Hours
        if (match[this.HOUR_GROUP].toLowerCase() === 'noon') {
            meridiem = 1;
            hour = 12;
        } else if (match[this.HOUR_GROUP].toLowerCase() === 'midnight') {
            meridiem = 0;
            hour = 0;
        } else {
            hour = parseInt(match[this.HOUR_GROUP], 10);
        }

        // ----- Minutes
        if (match[this.MINUTE_GROUP]) {
            minute = parseInt(match[this.MINUTE_GROUP], 10);
        } else if (hour > 100) {
            minute = hour % 100;
            hour = Math.floor(hour / 100);
        }

        if (minute >= 60) {
            return false;
        }

        if (hour > 24) {
            return false;
        }
        if (hour >= 12) {
            meridiem = 1;
        }

        return [hour, minute, second, millisecond, meridiem];
    }
}

import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {DAY, HOUR, MINUTE, MONTH, SECOND, WEEKDAY, YEAR} from "../../constants";
import {
    extractDateTimeUnitFragments,
    FragmentMap,
    FragmentName,
    TIME_UNIT_PATTERN,
    TIME_UNIT_STRICT_PATTERN
} from "../../utils/EN";
import * as moment from "moment";
import {Moment} from "moment";

export default class ENTimeLaterFormatParser extends Parser {
    private PATTERN: RegExp = new RegExp('' +
        '(\\W|^)' +
        '(' + TIME_UNIT_PATTERN + ')' +
        '(?:later|after|from now|henceforth|forward|out)(?=(?:\\W|$))', 'i');

    private STRICT_PATTERN: RegExp = new RegExp('' +
        '(\\W|^)' +
        '(' + TIME_UNIT_STRICT_PATTERN + ')' +
        '(?:later|from now)(?=(?:\\W|$))', 'i');

    private TAG: string = 'ENTimeLaterFormatParser';

    private OTHER_PATTERNS: RegExp[] = [
        /\w/
    ];

    pattern(): RegExp {
        return this.isStrictMode() ? this.STRICT_PATTERN : this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        if (match.index > 0 && text[match.index - 1].match(this.OTHER_PATTERNS[0])) return null;

        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length, match[0].length - match[1].length),
            index,
            ref
        });

        result.tags[this.TAG] = true;

        const momentRef: Moment = moment(ref);
        const fragments: FragmentMap = extractDateTimeUnitFragments(match[2]);

        (Object.keys(fragments) as FragmentName[]).forEach((fragment: FragmentName) => {
            momentRef.add(fragments[fragment]!, fragment);
        });

        if ((fragments['hour'] || 0) > 0 || (fragments['minute'] || 0) > 0 || (fragments['second'] || 0) > 0) {
            result.start.assign(HOUR, momentRef.hour());
            result.start.assign(MINUTE, momentRef.minute());
            result.start.assign(SECOND, momentRef.second());
        }

        if ((fragments['d'] || 0) > 0 || (fragments['month'] || 0) > 0 || (fragments['year'] || 0) > 0) {
            result.start.assign(DAY, momentRef.date());
            result.start.assign(MONTH, momentRef.month() + 1);
            result.start.assign(YEAR, momentRef.year());
        } else {
            if ((fragments['week'] || 0) > 0) {
                result.start.imply(WEEKDAY, momentRef.day());
            }

            result.start.imply(DAY, momentRef.date());
            result.start.imply(MONTH, momentRef.month() + 1);
            result.start.imply(YEAR, momentRef.year());
        }

        return result;
    }
}

import Parser from '../parser';
import {ParsedResult} from "../../result";
import {ParseOptions} from "../../chrono";
import {WEEKDAY_OFFSET} from '../../utils/EN';
import {Modifier, updateParsedComponent} from '../../utils/general';

export default class ENWeekdayParser extends Parser {
    private PATTERN: RegExp = new RegExp('(\\W|^)' +
        '(?:(?:\\,|\\(|\\（)\\s*)?' +
        '(?:on\\s*?)?' +
        '(?:(this|last|past|next)\\s*)?' +
        '(' + Object.keys(WEEKDAY_OFFSET).join('|') + ')' +
        '(?:\\s*(?:\\,|\\)|\\）))?' +
        '(?:\\s*(this|last|past|next)\\s*week)?' +
        '(?=\\W|$)', 'i');

    private PREFIX_GROUP: number = 2;
    private WEEKDAY_GROUP: number = 3;
    private POSTFIX_GROUP: number = 4;

    private TAG: string = 'ENWeekdayParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const matchedText: string = match[0].substr(match[1].length, match[0].length - match[1].length);
        const result: ParsedResult = new ParsedResult({
            text: matchedText,
            index,
            ref,
        });

        const offset: number | undefined = WEEKDAY_OFFSET[match[this.WEEKDAY_GROUP].toLowerCase()];
        if (offset === undefined) return null;

        const prefix: string = match[this.PREFIX_GROUP];
        const postfix: string = match[this.POSTFIX_GROUP];
        const norm: string = (prefix || postfix || '').toLowerCase();

        let modifier: Modifier = Modifier.UNKNOWN;
        if (norm === 'this') {
            modifier = Modifier.THIS;
        } else if (norm === 'last' || norm === 'past') {
            modifier = Modifier.LAST;
        } else if (norm === 'next') {
            modifier = Modifier.NEXT;
        }

        result.tags[this.TAG] = true;

        updateParsedComponent(result, ref, offset, modifier);

        return result;
    }
}

import * as moment from "moment";
import {Moment} from "moment";
import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {DAY, HOUR, MERIDIEM, MILLISECOND, MINUTE, MONTH, SECOND, YEAR} from "../../constants";

/*
  Valid patterns:
  - esta mañana -> today in the morning
  - esta tarde -> today in the afternoon/evening
  - esta noche -> tonight
  - ayer por la mañana -> yesterday in the morning
  - ayer por la tarde -> yesterday in the afternoon/evening
  - ayer por la noche -> yesterday at night
  - mañana por la mañana -> tomorrow in the morning
  - mañana por la tarde -> tomorrow in the afternoon/evening
  - mañana por la noche -> tomorrow at night
  - anoche -> tomorrow at night
  - hoy -> today
  - ayer -> yesterday
  - mañana -> tomorrow
 */

export default class ESCasualDateParser extends Parser {
    private PATTERN: RegExp = /(\W|^)(ahora|esta\s*(mañana|tarde|noche)|(ayer|mañana)\s*por\s*la\s*(mañana|tarde|noche)|hoy|mañana|ayer|anoche)(?=\W|$)/i;

    private OTHER_PATTERNS: RegExp[] = [
        /por\s*la/,
        /ahora/
    ];

    private TAG: string = 'ESCasualDateParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length),
            index,
            ref
        });

        const refMoment: Moment = moment(ref);
        const startMoment: Moment = refMoment.clone();
        const matchedText = result.text.toLowerCase();

        if (matchedText === 'mañana') {
            // Check not "Tomorrow" on late night
            if (refMoment.hour() > 1) {
                startMoment.add(1, 'day');
            }
        } else if (matchedText === 'ayer') {
            startMoment.add(-1, 'day');
        } else if (matchedText === 'anoche') {
            result.start.imply(HOUR, 0);
            if (refMoment.hour() > 6) {
                startMoment.add(-1, 'day');
            }
        } else if (matchedText.match("esta")) {
            const secondMatch: string = match[3].toLowerCase();
            if (secondMatch === "tarde") {
                result.start.imply(HOUR, 18);
            } else if (secondMatch === "mañana") {
                result.start.imply(HOUR, 6);
            } else if (secondMatch === "noche") {
                // Normally means this coming midnight
                result.start.imply(HOUR, 22);
                result.start.imply(MERIDIEM, 1);
            }
        } else if (matchedText.match(this.OTHER_PATTERNS[0])) {
            const firstMatch: string = match[4].toLowerCase();
            if (firstMatch === 'ayer') {
                startMoment.add(-1, 'day');
            } else if (firstMatch === 'mañana') {
                startMoment.add(1, 'day');
            }
            const secondMatch: string = match[5].toLowerCase();
            if (secondMatch === "tarde") {
                result.start.imply(HOUR, 18);
            } else if (secondMatch === "mañana") {
                result.start.imply(HOUR, 9);
            } else if (secondMatch === "noche") {
                // Normally means this coming midnight
                result.start.imply(HOUR, 22);
                result.start.imply(MERIDIEM, 1);
            }
        } else if (matchedText.match(this.OTHER_PATTERNS[1])) {
            result.start.imply(HOUR, refMoment.hour());
            result.start.imply(MINUTE, refMoment.minute());
            result.start.imply(SECOND, refMoment.second());
            result.start.imply(MILLISECOND, refMoment.millisecond());
        }

        result.start.assign(DAY, startMoment.date());
        result.start.assign(MONTH, startMoment.month() + 1);
        result.start.assign(YEAR, startMoment.year());
        result.tags[this.TAG] = true;

        return result;
    }
}

import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import * as moment from "moment";
import {UnitOfTime} from "../../constants";
import {deadlineCalculations} from "../../utils/general";
import {matchNumber, matchUnit} from "../../utils/ES";

export default class ESDeadlineFormatParser extends Parser {
    private PATTERN: RegExp = /(\W|^)(dentro\s*de|en)\s*([0-9]+|medi[oa]|una?)\s*(minutos?|horas?|d[ií]as?)\s*(?=(?:\W|$))/i;

    private NUM_MATCH: number = 3;
    private UNIT_MATCH: number = 4;

    private TAG: string = 'ESDeadlineFormatParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length, match[0].length - match[1].length),
            index,
            ref
        });

        result.tags[this.TAG] = true;

        const num: number = matchNumber(match[this.NUM_MATCH].toLowerCase());
        const matchedUnit: UnitOfTime | undefined = matchUnit(match[this.UNIT_MATCH].toLowerCase());

        if (num && matchedUnit && deadlineCalculations(num, matchedUnit, result, moment(ref))) {
            return result;
        }

        return null;
    }
}

import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {MONTH_OFFSET, WEEKDAY_OFFSET, yearCalculation} from "../../utils/ES";
import {DAY, MONTH, WEEKDAY, YEAR} from "../../constants";
import {getAppropriateYear} from "../../utils/general";

export default class ESMonthNameLittleEndianParser extends Parser {
    private PATTERN: RegExp = new RegExp('(\\W|^)' +
        '(?:(Domingo|Lunes|Martes|Miércoles|Miercoles|Jueves|Viernes|Sábado|Sabado|Dom|Lun|Mar|Mie|Jue|Vie|Sab)\\s*,?\\s*)?' +
        '([0-9]{1,2})(?:º|ª|°)?' +
        '(?:\\s*(?:desde|de|\\-|\\–|al?|hasta|\\s)\\s*([0-9]{1,2})(?:º|ª|°)?)?\\s*(?:de)?\\s*' +
        '(Ene(?:ro|\\.)?|Feb(?:rero|\\.)?|Mar(?:zo|\\.)?|Abr(?:il|\\.)?|May(?:o|\\.)?|Jun(?:io|\\.)?|Jul(?:io|\\.)?|Ago(?:sto|\\.)?|Sep(?:tiembre|\\.)?|Set(?:iembre|\\.)?|Oct(?:ubre|\\.)?|Nov(?:iembre|\\.)?|Dic(?:iembre|\\.)?)' +
        '(?:\\s*(?:del?)?(\\s*[0-9]{1,4}(?![^\\s]\\d))(\\s*[ad]\\.?\\s*c\\.?|a\\.?\\s*d\\.?)?)?' +
        '(?=\\W|$)', 'i'
    );

    private WEEKDAY_GROUP: number = 2;
    private DATE_GROUP: number = 3;
    private DATE_TO_GROUP: number = 4;
    private MONTH_NAME_GROUP: number = 5;
    private YEAR_GROUP: number = 6;
    private YEAR_BE_GROUP: number = 7;

    private TAG: string = 'ESMonthNameLittleEndianParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length, match[0].length - match[1].length),
            index,
            ref
        });

        result.tags[this.TAG] = true;

        const month: number = MONTH_OFFSET[match[this.MONTH_NAME_GROUP].toLowerCase()];

        if (!(month || month === 0)) {
            return null;
        }

        const day: number = parseInt(match[this.DATE_GROUP], 10);

        if (!day) {
            return null;
        }

        result.start.assign(DAY, day);
        result.start.assign(MONTH, month);

        let year: number | null = yearCalculation(match[this.YEAR_GROUP], match[this.YEAR_BE_GROUP]);

        if (year) {
            result.start.assign(YEAR, year);
        } else {
            getAppropriateYear(result.start, ref);
        }

        // Weekday component
        if (match[this.WEEKDAY_GROUP]) {
            const weekday: number = WEEKDAY_OFFSET[match[this.WEEKDAY_GROUP].toLowerCase()];
            if (weekday || weekday === 0) {
                result.start.assign(WEEKDAY, weekday);
            }
        }

        // Text can be 'range' value. Such as '12 - 13 January 2012'
        if (match[this.DATE_TO_GROUP]) {
            const endDate = parseInt(match[this.DATE_TO_GROUP], 10);

            if (endDate) {
                result.end = result.start.clone();
                result.end.assign(DAY, endDate);
            }
        }

        return result;
    }
}

/*
    Date format with slash "/" (also "-" and ".") between numbers
    - Martes 3/11/2015
    - 3/11/2015
    - 3/11
*/

import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {DAY, MONTH, WEEKDAY, YEAR} from "../../constants";
import {WEEKDAY_OFFSET} from "../../utils/ES";
import {checkMonthDaysValid} from "../../utils/general";
import * as moment from 'moment';

export default class ESSlashDateFormatParser extends Parser {
    private PATTERN: RegExp = new RegExp('(\\W|^)' +
        '(?:' +
        '((?:domingo|dom|lunes|lun|martes|mar|mi[ée]rcoles|mie|jueves|jue|viernes|vie|s[áa]bado|sab))' +
        '\\s*\\,?\\s*' +
        ')?' +
        '([0-1]{0,1}[0-9]{1})[\\/\\.\\-]([0-3]{0,1}[0-9]{1})' +
        '(?:' +
        '[\\/\\.\\-]' +
        '([0-9]{4}\s*\,?\s*|[0-9]{2}\s*\,?\s*)' +
        ')?' +
        '(\\W|$)', 'i');

    private OPENING_GROUP: number = 1;
    private ENDING_GROUP: number = 6;

    private WEEKDAY_GROUP: number = 2;
    private MONTH_GROUP: number = 4;
    private DAY_GROUP: number = 3;
    private YEAR_GROUP: number = 5;

    private TAG: string = 'ESSlashDateFormatParser';

    private OTHER_PATTERNS: RegExp[] = [
        /^\d\.\d$/,
        /^\d\.\d{1,2}\.\d{1,2}$/
    ];

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        if (match[this.OPENING_GROUP] == '/' || match[this.ENDING_GROUP] == '/') {
            // Long skip, if there is some overlapping like:
            // XX[/YY/ZZ]
            // [XX/YY/]ZZ
            match.index += match[0].length;
            return null;
        }

        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[this.OPENING_GROUP].length, match[0].length - match[this.ENDING_GROUP].length),
            index,
            ref
        });

        result.tags[this.TAG] = true;
        const matchedText: string = result.text.toLowerCase();

        if (matchedText.match(this.OTHER_PATTERNS[0]) || matchedText.match(this.OTHER_PATTERNS[1])) {
            return null;
        }

        // MM/dd -> OK
        // MM.dd -> NG
        if (!match[this.YEAR_GROUP] && match[0].indexOf('/') < 0) {
            return null;
        }

        let year: number = match[this.YEAR_GROUP] ? parseInt(match[this.YEAR_GROUP], 10) : moment(ref).year();
        let month: number = parseInt(match[this.MONTH_GROUP], 10);
        let day: number = parseInt(match[this.DAY_GROUP], 10);

        if (month < 1 || day < 1 || day > 31) {
            return null
        }

        if (year < 100) {
            if (year > 50) {
                year = year + 1900;
            } else {
                year = year + 2000;
            }
        }

        if (month > 12) {
            if (month <= 31 && day <= 12 && checkMonthDaysValid(month, day, year)) {
                [month, day] = [day, month];
            } else {
                return null;
            }
        }

        result.start.assign(DAY, day);
        result.start.assign(MONTH, month);
        if (match[this.YEAR_GROUP]) {
            result.start.assign(YEAR, year);
        } else {
            result.start.imply(YEAR, year);
        }

        if (match[this.WEEKDAY_GROUP]) {
            result.start.assign(WEEKDAY, WEEKDAY_OFFSET[match[this.WEEKDAY_GROUP].toLowerCase()]);
        }

        return result;
    }
}

import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {DAY, HOUR, MINUTE, MONTH, UnitOfTime, WEEK, WEEKDAY, YEAR} from "../../constants";
import {matchNumber, matchUnit} from "../../utils/ES";
import * as moment from "moment";
import {Moment} from "moment";

enum Mode {
    NONE,
    TIME,
    WEEK,
    DATE
}

export default class ESTimeAgoFormatParser extends Parser {
    private PATTERN: RegExp = /(\W|^)hace\s*([0-9]+|medi[oa]|una?)\s*(minutos?|horas?|semanas?|d[ií]as?|mes(es)?|años?)(?=(?:\W|$))/i;

    private TAG: string = 'ESTimeAgoFormatParser';

    private OTHER_PATTERNS: RegExp[] = [
        /\w/
    ];

    private NUM_MATCH: number = 2;
    private UNIT_MATCH: number = 3;

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        if (match.index > 0 && text[match.index - 1].match(this.OTHER_PATTERNS[0])) return null;

        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length, match[0].length - match[1].length),
            index,
            ref
        });

        result.tags[this.TAG] = true;

        const num: number = matchNumber(match[this.NUM_MATCH]);
        const matchedUnit: UnitOfTime | undefined = matchUnit(match[this.UNIT_MATCH].toLowerCase());
        let mode: Mode = Mode.NONE;
        const momentRef: Moment = moment(ref);

        switch (matchedUnit) {
            case HOUR:
                momentRef.add(-num, 'hour');
                mode = Mode.TIME;
                break;
            case MINUTE:
                momentRef.add(-num, 'minute');
                mode = Mode.TIME;
                break;
            case WEEK:
                momentRef.add(-num, 'week');
                mode = Mode.WEEK;
                break;
            case DAY:
                momentRef.add(-num, 'd');
                mode = Mode.DATE;
                break;
            case MONTH:
                momentRef.add(-num, 'month');
                mode = Mode.DATE;
                break;
            case YEAR:
                momentRef.add(-num, 'year');
                mode = Mode.DATE;
                break;
            default:
                return null;
        }

        switch (mode) {
            case Mode.TIME:
                result.start.imply(DAY, momentRef.date());
                result.start.imply(MONTH, momentRef.month() + 1);
                result.start.imply(YEAR, momentRef.year());
                result.start.assign(HOUR, momentRef.hour());
                result.start.assign(MINUTE, momentRef.minute());
                break;
            case Mode.DATE:
                result.start.assign(DAY, momentRef.date());
                result.start.assign(MONTH, momentRef.month() + 1);
                result.start.assign(YEAR, momentRef.year());
                break;
            case Mode.WEEK:
                result.start.imply(DAY, momentRef.date());
                result.start.imply(MONTH, momentRef.month() + 1);
                result.start.imply(YEAR, momentRef.year());
                result.start.imply(WEEKDAY, momentRef.day());
                break;
            default:
                return null;
        }

        return result;
    }
}

import Parser from '../parser';
import {ParsedComponents, ParsedResult} from "../../result";
import {ParseOptions} from "../../chrono";
import {DAY, HOUR, MERIDIEM, MINUTE, MONTH, SECOND, YEAR} from "../../constants";
import * as moment from "moment";
import {Moment} from "moment";

type Time = [number, number, number, number];

export default class ESTimeExpressionParser extends Parser {
    private PATTERN: RegExp = new RegExp("(^|\\s|T)" +
    "(?:(?:a las?|al?|desde|de)\\s*)?" +
    "(\\d{1,4}|mediod[ií]a|medianoche)" +
    "(?:" +
        "(?:\\.|\\:|\\：)(\\d{1,2})" +
        "(?:" +
            "(?:\\:|\\：)(\\d{2})" +
        ")?" +
    ")?" +
    "(?:\\s*(A\\.M\\.|P\\.M\\.|AM?|PM?))?" +
    "(?=\\W|$)", 'i');

    private END_PATTERN: RegExp = new RegExp("^\\s*" +
    "(\\-|\\–|\\~|\\〜|a(?:\s*las)?|\\?)\\s*" +
    "(\\d{1,4})" +
    "(?:" +
        "(?:\\.|\\:|\\：)(\\d{1,2})" +
        "(?:" +
            "(?:\\.|\\:|\\：)(\\d{1,2})" +
        ")?" +
    ")?" +
    "(?:\\s*(A\\.M\\.|P\\.M\\.|AM?|PM?))?" +
    "(?=\\W|$)", 'i');

    private HOUR_GROUP = 2;
    private MINUTE_GROUP = 3;
    private SECOND_GROUP = 4;
    private AM_PM_HOUR_GROUP = 5;

    private TAG: string = 'ESTimeExpressionParser';

    private OTHER_PATTERNS: RegExp[] = [
        /\w/,
        /^\s*([+\-])\s*\d{3,4}$/,
        /^\d+$/,
        /mediod/
    ];

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        // This pattern can be overlaped Ex. [12] AM, 1[2] AM
        if (match.index > 0 && text[match.index - 1].match(this.OTHER_PATTERNS[0])) return null;

        const refMoment: Moment = moment(ref);

        const result: ParsedResult = new ParsedResult({
            text: match[0].substring(match[1].length),
            index: match.index + match[1].length,
            ref
        });

        result.tags[this.TAG] = true;

        result.start.imply(DAY, refMoment.date());
        result.start.imply(MONTH, refMoment.month() + 1);
        result.start.imply(YEAR, refMoment.year());

        if (!this.extractFirstChunk(result, match)) {
            return null;
        }

        if (!this.extractSecondChunk(text, result) && result.text.match(this.OTHER_PATTERNS[2])) {
            return null;
        }

        return result;
    }

    private extractFirstChunk(result: ParsedResult, match: RegExpExecArray): boolean {
        const time: Time | false = this.getTime(match);
        if (!time) {
            return false;
        }

        let [hour, minute, second, meridiem]: Time = time;

        if (second > -1) {
            result.start.assign(SECOND, second);
        }


        // ----- AM & PM
        if (match[this.AM_PM_HOUR_GROUP]) {
            if (hour > 12) return false;
            const ampm: string = match[this.AM_PM_HOUR_GROUP][0].toLowerCase();
            if (ampm === 'a') {
                meridiem = 0;
                if (hour === 12) hour = 0;
            } else if (ampm === 'p') {
                meridiem = 1;
                if (hour !== 12) hour += 12;
            }
        }

        result.start.assign(HOUR, hour);
        result.start.assign(MINUTE, minute);

        if (meridiem >= 0) {
            result.start.assign(MERIDIEM, meridiem);
        } else {
            if (hour < 12) {
                result.start.imply(MERIDIEM, 0);
            } else {
                result.start.imply(MERIDIEM, 1);
            }
        }

        return true;
    }

    private extractSecondChunk(text: string, result: ParsedResult): boolean {
        const match: RegExpExecArray | null = this.END_PATTERN.exec(text.substring(result.index + result.text.length));

        if (!match) {
            return false;
        }

        // Pattern "YY.YY -XXXX" is more like timezone offset
        if (match[0].match(this.OTHER_PATTERNS[1])) {
            return true;
        }

        const time: Time | false = this.getTime(match);
        if (!time) {
            return true;
        }

        let [hour, minute, second, meridiem]: Time = time;

        if (!result.end) {
            result.end = new ParsedComponents(undefined, result.start.date());
        }

        if (second > -1) {
            result.end.assign(SECOND, second);
        }

        // ----- AM & PM
        if (match[this.AM_PM_HOUR_GROUP]) {

            if (hour > 12) return false;

            const ampm: string = match[this.AM_PM_HOUR_GROUP][0].toLowerCase();
            if (ampm === 'a') {
                meridiem = 0;
                if (hour === 12) {
                    hour = 0;
                    if (!result.end.isCertain(DAY)) {
                        result.end.imply(DAY, result.end.get(DAY) + 1);
                    }
                }
            }

            if (ampm === 'p') {
                meridiem = 1;
                if (hour !== 12) hour += 12;
            }

            if (!result.start.isCertain(MERIDIEM)) {
                if (meridiem === 0) {
                    result.start.imply(MERIDIEM, 0);

                    if (result.start.get(HOUR) === 12) {
                        result.start.assign(HOUR, 0);
                    }
                } else {
                    result.start.imply(MERIDIEM, 1);

                    if (result.start.get(HOUR) !== 12) {
                        result.start.assign(HOUR, result.start.get(HOUR) + 12);
                    }
                }
            }
        }

        result.text = result.text + match[0];
        result.end.assign(HOUR, hour);
        result.end.assign(MINUTE, minute);
        if (meridiem >= 0) {
            result.end.assign(MERIDIEM, meridiem);
        }

        if (result.end.date().getTime() < result.start.date().getTime()) {
            result.end.imply(DAY, result.end.get(DAY) + 1);
        }

        return true;
    }

    private getTime(match: RegExpExecArray): Time | false {
        let second: number = -1;
        let hour: number = 0;
        let minute: number = 0;
        let meridiem: number = -1;

        // ----- Second
        if (match[this.SECOND_GROUP]) {
            second = parseInt(match[this.SECOND_GROUP], 10);
            if (second >= 60) return false;
        }

        // ----- Hours
        if (match[this.HOUR_GROUP].toLowerCase().match(this.OTHER_PATTERNS[3])) {
            meridiem = 1;
            hour = 12;
        } else if (match[this.HOUR_GROUP].toLowerCase() === 'medianoche') {
            meridiem = 0;
            hour = 0;
        } else {
            hour = parseInt(match[this.HOUR_GROUP], 10);
        }

        // ----- Minutes
        if (match[this.MINUTE_GROUP]) {
            minute = parseInt(match[this.MINUTE_GROUP], 10);
        } else if (hour > 100) {
            minute = hour % 100;
            hour = Math.floor(hour / 100);
        }

        if (minute >= 60) {
            return false;
        }

        if (hour > 24) {
            return false;
        }
        if (hour >= 12) {
            meridiem = 1;
        }

        return [hour, minute, second, meridiem];
    }
}

import Parser from '../parser';
import {ParsedResult} from "../../result";
import {ParseOptions} from "../../chrono";
import {WEEKDAY_OFFSET} from '../../utils/ES';
import {Modifier, updateParsedComponent} from '../../utils/general';

export default class ESWeekdayParser extends Parser {
    private PATTERN: RegExp = new RegExp('(\\W|^)' +
        '(?:(?:\\,|\\(|\\（)\\s*)?' +
        '(?:(este|pasado|pr[oó]ximo)\\s*)?' +
        '(' + Object.keys(WEEKDAY_OFFSET).join('|') + ')' +
        '(?:\\s*(?:\\,|\\)|\\）))?' +
        '(?:\\s*(este|pasado|pr[óo]ximo)\\s*week)?' +
        '(?=\\W|$)', 'i');

    private PREFIX_GROUP: number = 2;
    private WEEKDAY_GROUP: number = 3;
    private POSTFIX_GROUP: number = 4;

    private TAG: string = 'ESWeekdayParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const matchedText: string = match[0].substr(match[1].length, match[0].length - match[1].length);
        const result: ParsedResult = new ParsedResult({
            text: matchedText,
            index,
            ref,
        });

        const offset: number | undefined = WEEKDAY_OFFSET[match[this.WEEKDAY_GROUP].toLowerCase()];
        if (offset === undefined) return null;

        const prefix: string = match[this.PREFIX_GROUP];
        const postfix: string = match[this.POSTFIX_GROUP];
        const norm: string = (prefix || postfix || '').toLowerCase();

        let modifier: Modifier = Modifier.UNKNOWN;
        if (norm== 'este') {
            modifier = Modifier.THIS;
        } else if (norm === 'pasado') {
            modifier = Modifier.LAST;
        } else if (norm == 'próximo' || norm == 'proximo') {
            modifier = Modifier.NEXT;
        }

        result.tags[this.TAG] = true;

        updateParsedComponent(result, ref, offset, modifier);

        return result;
    }
}

import * as moment from "moment";
import {Moment} from "moment";
import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {DAY, HOUR, MERIDIEM, MILLISECOND, MINUTE, MONTH, SECOND, YEAR} from "../../constants";

export default class FRCasualDateParser extends Parser {
    private PATTERN: RegExp = /(\W|^)(maintenant|aujourd'hui|ajd|cette\s*nuit|la\s*veille|(demain|hier)(\s*(matin|soir|aprem|après-midi))?|ce\s*(matin|soir)|cet\s*(après-midi|aprem))(?=\W|$)/i;

    private OTHER_PATTERNS: RegExp[] = [
        /demain/,
        /hier/,
        /cette\s*nuit/,
        /la\s*veille/,
        /après-midi|aprem/,
        /soir/,
        /matin/,
        /maintenant/
    ];

    private TAG: string = 'FRCasualDateParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length),
            index,
            ref
        });

        const refMoment: Moment = moment(ref);
        const startMoment: Moment = refMoment.clone();
        const matchedText = result.text.toLowerCase();

        if (matchedText.match(this.OTHER_PATTERNS[0])) {
            if (refMoment.hour() > 1) {
                startMoment.add(1, 'day');
            }
        } else if (matchedText.match(this.OTHER_PATTERNS[1])) {
            startMoment.add(-1, 'day');
        } else if (matchedText.match(this.OTHER_PATTERNS[2])) {
            result.start.imply(HOUR, 22);
            result.start.imply(MERIDIEM, 1);
        } else if (matchedText.match(this.OTHER_PATTERNS[3])) {
            result.start.imply(HOUR, 0);
            if (refMoment.hour() > 6) {
                startMoment.add(-1, 'day');
            }
        } else if (matchedText.match(this.OTHER_PATTERNS[4])) {
            result.start.imply(HOUR, 14);
        } else if (matchedText.match(this.OTHER_PATTERNS[5])) {
            result.start.imply(HOUR, 18);
        } else if (matchedText.match(this.OTHER_PATTERNS[6])) {
            result.start.imply(HOUR, 8);
        } else if (matchedText.match(this.OTHER_PATTERNS[7])) {
            result.start.assign(HOUR, refMoment.hour());
            result.start.assign(MINUTE, refMoment.minute());
            result.start.assign(SECOND, refMoment.second());
            result.start.assign(MILLISECOND, refMoment.millisecond());

        }

        result.start.assign(DAY, startMoment.date());
        result.start.assign(MONTH, startMoment.month() + 1);
        result.start.assign(YEAR, startMoment.year());
        result.tags[this.TAG] = true;
        return result;
    }
}

import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import * as moment from "moment";
import {UnitOfTime} from "../../constants";
import {deadlineCalculations} from "../../utils/general";
import {INTEGER_WORDS_PATTERN, matchNumber, matchUnit} from "../../utils/FR";

export default class FRDeadlineFormatParser extends Parser {
    private PATTERN: RegExp = new RegExp('(\\W|^)' +
        '(dans|en)\\s*' +
        '('+ INTEGER_WORDS_PATTERN + '|[0-9]+|une?|(?:\\s*quelques)?|demi(?:\\s*|-?)?)\\s*' +
        '(secondes?|min(?:ute)?s?|heures?|jours?|semaines?|mois|années?)\\s*' +
        '(?=\\W|$)', 'i'
    );

    private STRICT_PATTERN: RegExp = new RegExp('(\\W|^)' +
        '(dans|en)\\s*' +
        '('+ INTEGER_WORDS_PATTERN + '|[0-9]+|un?)\\s*' +
        '(secondes?|minutes?|heures?|jours?)\\s*' +
        '(?=\\W|$)', 'i'
    );

    private NUM_MATCH: number = 3;
    private UNIT_MATCH: number = 4;

    private TAG: string = 'FRDeadlineFormatParser';

    pattern(): RegExp {
        return this.isStrictMode()? this.STRICT_PATTERN : this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length, match[0].length - match[1].length),
            index,
            ref
        });

        result.tags[this.TAG] = true;

        const num: number = matchNumber(match[this.NUM_MATCH].toLowerCase());
        const matchedUnit: UnitOfTime | undefined = matchUnit(match[this.UNIT_MATCH].toLowerCase());

        if (num && matchedUnit && deadlineCalculations(num, matchedUnit, result, moment(ref))) {
            return result;
        }

        return null;
    }
}

import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {MONTH_OFFSET, WEEKDAY_OFFSET, yearCalculation} from "../../utils/FR";
import {DAY, MONTH, WEEKDAY, YEAR} from "../../constants";
import {getAppropriateYear} from "../../utils/general";

export default class FRMonthNameLittleEndianParser extends Parser {
    private PATTERN: RegExp = new RegExp('(\\W|^)' +
        '(?:(Dimanche|Lundi|Mardi|mercredi|Jeudi|Vendredi|Samedi|Dim|Lun|Mar|Mer|Jeu|Ven|Sam)\\s*,?\\s*)?' +
        '([0-9]{1,2}|1er)' +
        '(?:\\s*(?:au|\\-|\\–|jusqu\'au?|\\s)\\s*([0-9]{1,2})(?:er)?)?\\s*(?:de)?\\s*' +
        '(Jan(?:vier|\\.)?|F[ée]v(?:rier|\\.)?|Mars|Avr(?:il|\\.)?|Mai|Juin|Juil(?:let|\\.)?|Ao[uû]t|Sept(?:embre|\\.)?|Oct(?:obre|\\.)?|Nov(?:embre|\\.)?|d[ée]c(?:embre|\\.)?)' +
        '(?:\\s*(\\s*[0-9]{1,4}(?![^\\s]\\d))(?:\\s*(AC|[ap]\\.?\\s*c(?:h(?:r)?)?\\.?\\s*n\\.?))?)?' +
        '(?=\\W|$)', 'i'
    );

    private WEEKDAY_GROUP: number = 2;
    private DATE_GROUP: number = 3;
    private DATE_TO_GROUP: number = 4;
    private MONTH_NAME_GROUP: number = 5;
    private YEAR_GROUP: number = 6;
    private YEAR_BE_GROUP: number = 7;

    private TAG: string = 'FRMonthNameLittleEndianParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length, match[0].length - match[1].length),
            index,
            ref
        });

        result.tags[this.TAG] = true;

        const month: number = MONTH_OFFSET[match[this.MONTH_NAME_GROUP].toLowerCase()];

        if (!(month || month === 0)) {
            return null;
        }

        const day: number = parseInt(match[this.DATE_GROUP], 10);

        if (!day) {
            return null;
        }

        result.start.assign(DAY, day);
        result.start.assign(MONTH, month);

        let year: number | null = yearCalculation(match[this.YEAR_GROUP], match[this.YEAR_BE_GROUP]);

        if (year) {
            result.start.assign(YEAR, year);
        } else {
            getAppropriateYear(result.start, ref);
        }

        // Weekday component
        if (match[this.WEEKDAY_GROUP]) {
            const weekday: number = WEEKDAY_OFFSET[match[this.WEEKDAY_GROUP].toLowerCase()];
            if (weekday || weekday === 0) {
                result.start.assign(WEEKDAY, weekday);
            }
        }

        // Text can be 'range' value. Such as '12 - 13 January 2012'
        if (match[this.DATE_TO_GROUP]) {
            const endDate = parseInt(match[this.DATE_TO_GROUP], 10);

            if (endDate) {
                result.end = result.start.clone();
                result.end.assign(DAY, endDate);
            }
        }

        return result;
    }
}

import * as moment from "moment";
import {Moment, unitOfTime} from "moment";
import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {matchNumber, matchUnit, INTEGER_WORDS_PATTERN} from "../../utils/FR";
import {DAY, HOUR, MILLISECOND, MINUTE, MONTH, SECOND, UnitOfTime, WEEK, YEAR} from "../../constants";

// Force load fr localization data from moment for the locale files to be linkded durning browserify.
// NOTE: The function moment.defineLocale() also has a side effect that it change global locale
//  We also need to save and restore the previous locale (see. moment.js, loadLocale)
const originalLocale = moment.locale();
const ignored = require('moment/locale/fr');
moment.locale(originalLocale);

export default class FRRelativeDateFormatParser extends Parser {
    private PATTERN: RegExp = new RegExp('(\\W|^)' +
        '(?:les?|la|l\'|du|des?)\\s*' +
        '('+ INTEGER_WORDS_PATTERN + '|\\d+)?\\s*' +
        '(prochaine?s?|derni[eè]re?s?|pass[ée]e?s?|pr[ée]c[ée]dents?|suivante?s?)?\\s*' +
        '(secondes?|min(?:ute)?s?|heures?|jours?|semaines?|mois|trimestres?|années?)\\s*' +
        '(prochaine?s?|derni[eè]re?s?|pass[ée]e?s?|pr[ée]c[ée]dents?|suivante?s?)?' +
        '(?=\\W|$)', 'i'
    );

    private MULTIPLIER_GROUP: number = 2;
    private MODIFIER_1_GROUP: number = 3;
    private RELATIVE_WORD_GROUP: number = 4;
    private MODIFIER_2_GROUP: number = 5;

    private OTHER_PATTERNS: RegExp[] = [
        /prochaine?s?|suivants?/,
        /derni[eè]re?s?|pass[ée]e?s?|pr[ée]c[ée]dents?/
    ];

    private TAG: string = 'FRRelativeDateFormatParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length, match[0].length - match[1].length),
            index,
            ref
        });

        result.tags[this.TAG] = true;


        const matchedMultiplier: string = match[this.MULTIPLIER_GROUP];
        const num: number = matchNumber(matchedMultiplier);

        const matchedModifier: string = (match[this.MODIFIER_1_GROUP] || match[this.MODIFIER_2_GROUP] || '').toLowerCase();
        if (!matchedModifier) {
            return null;
        }

        let modifierFactor: number;

        if (this.OTHER_PATTERNS[0].test(matchedModifier)) {
            modifierFactor = 1;
        } else if (this.OTHER_PATTERNS[1].test(matchedModifier)) {
            modifierFactor = -1;
        } else {
            return null;
        }

        const total: number = modifierFactor * (num || 1);
        let dateFrom: Moment = moment(ref).locale('fr');
        let dateTo: Moment = dateFrom.clone();
        const unit: UnitOfTime | undefined = matchUnit((match[this.RELATIVE_WORD_GROUP] || '').toLowerCase());

        if (!unit) {
            return null;
        }

        let startOf: unitOfTime.StartOf;

        switch (unit) {
            case SECOND:
                dateFrom.add(total, 's');
                dateTo.add(modifierFactor, 's');
                startOf = 'second';
                break;
            case MINUTE:
                dateFrom.add(total, 'm');
                dateTo.add(modifierFactor, 'm');
                startOf = 'minute';
                break;
            case HOUR:
                dateFrom.add(total, 'h');
                dateTo.add(modifierFactor, 'h');
                startOf = 'hour';
                break;
            case DAY:
                dateFrom.add(total, 'd');
                dateTo.add(modifierFactor, 'd');
                startOf = 'day';
                break;
            case WEEK:
                dateFrom.add(total, 'w');
                dateTo.add(modifierFactor, 'w');
                startOf = 'week';
                break;
            case MONTH:
                dateFrom.add(total, 'M');
                dateTo.add(modifierFactor, 'M');
                startOf = 'month';
                break;
            case YEAR:
                dateFrom.add(total, 'y');
                dateTo.add(modifierFactor, 'y');
                startOf = 'year';
                break;
            default:
                return null;
        }

        if (modifierFactor > 0) {
            [dateFrom, dateTo] = [dateTo, dateFrom];
        }

        // Get start and end of dates
        dateFrom.startOf(startOf);
        dateTo.endOf(startOf);

        result.start.assign(YEAR, dateFrom.year());
        result.start.assign(MONTH, dateFrom.month() + 1);
        result.start.assign(DAY, dateFrom.date());
        result.start.assign(MINUTE, dateFrom.minute());
        result.start.assign(SECOND, dateFrom.second());
        result.start.assign(HOUR, dateFrom.hour());
        result.start.assign(MILLISECOND, dateFrom.millisecond());

        result.end = result.start.clone();
        result.end.assign(YEAR, dateTo.year());
        result.end.assign(MONTH, dateTo.month() + 1);
        result.end.assign(DAY, dateTo.date());
        result.end.assign(MINUTE, dateTo.minute());
        result.end.assign(SECOND, dateTo.second());
        result.end.assign(HOUR, dateTo.hour());
        result.end.assign(MILLISECOND, dateTo.millisecond());

        return result;
    }
}

/*
    Date format with slash "/" (also "-" and ".") between numbers
    - Martes 3/11/2015
    - 3/11/2015
    - 3/11
*/
import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {DAY, MONTH, WEEKDAY, YEAR} from "../../constants";
import {WEEKDAY_OFFSET} from "../../utils/FR";
import {checkMonthDaysValid, getAppropriateYear} from "../../utils/general";
import * as moment from 'moment';

export default class FRSlashDateFormatParser extends Parser {
    private PATTERN: RegExp = new RegExp('(\\W|^)' +
        '(?:' +
        '((?:dimanche|dim|lundi|lun|mardi|mar|mercredi|mer|jeudi|jeu|vendredi|ven|samedi|sam|le))' +
        '\\s*\\,?\\s*' +
        ')?' +
        '([0-3]{0,1}[0-9]{1})[\\/\\.\\-]([0-3]{0,1}[0-9]{1})' +
        '(?:' +
        '[\\/\\.\\-]' +
        '([0-9]{4}\s*\,?\s*|[0-9]{2}\s*\,?\s*)' +
        ')?' +
        '(\\W|$)', 'i');

    private OPENING_GROUP: number = 1;
    private ENDING_GROUP: number = 6;

    private WEEKDAY_GROUP: number = 2;
    private MONTH_GROUP: number = 4;
    private DAY_GROUP: number = 3;
    private YEAR_GROUP: number = 5;

    private TAG: string = 'FRSlashDateFormatParser';

    private OTHER_PATTERNS: RegExp[] = [
        /^\d\.\d$/,
        /^\d\.\d{1,2}\.\d{1,2}$/
    ];

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        if (match[this.OPENING_GROUP] == '/' || match[this.ENDING_GROUP] == '/') {
            // Long skip, if there is some overlapping like:
            // XX[/YY/ZZ]
            // [XX/YY/]ZZ
            match.index += match[0].length;
            return null;
        }

        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[this.OPENING_GROUP].length, match[0].length - match[this.ENDING_GROUP].length),
            index,
            ref
        });

        result.tags[this.TAG] = true;
        const matchedText: string = result.text.toLowerCase();

        if (matchedText.match(this.OTHER_PATTERNS[0]) || matchedText.match(this.OTHER_PATTERNS[1])) {
            return null;
        }

        // MM/dd -> OK
        // MM.dd -> NG
        if (!match[this.YEAR_GROUP] && match[0].indexOf('/') < 0) {
            return null;
        }

        let year: number = match[this.YEAR_GROUP] ? parseInt(match[this.YEAR_GROUP], 10) : moment(ref).year();
        let month: number = parseInt(match[this.MONTH_GROUP], 10);
        let day: number = parseInt(match[this.DAY_GROUP], 10);

        if (month < 1 || day < 1 || day > 31) {
            return null;
        }

        if (year < 100) {
            year = year + 2000;
        }

        if (month > 12) {
            if (month <= 31 && day <= 12 && checkMonthDaysValid(month, day, year)) {
                [month, day] = [day, month];
            } else {
                return null;
            }
        }

        result.start.assign(DAY, day);
        result.start.assign(MONTH, month);
        if (match[this.YEAR_GROUP]) {
            result.start.assign(YEAR, year);
        } else {
            getAppropriateYear(result.start, ref);
        }

        if (match[this.WEEKDAY_GROUP]) {
            result.start.assign(WEEKDAY, WEEKDAY_OFFSET[match[this.WEEKDAY_GROUP].toLowerCase()]);
        }

        return result;
    }
}

import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {DAY, HOUR, MINUTE, MONTH, UnitOfTime, WEEK, WEEKDAY, YEAR} from "../../constants";
import {matchNumber, matchUnit} from "../../utils/FR";
import * as moment from "moment";
import {Moment} from "moment";

enum Mode {
    NONE,
    TIME,
    WEEK,
    DATE
}

export default class FRTimeAgoFormatParser extends Parser {
    private PATTERN: RegExp = /(\W|^)il y a\s*([0-9]+|une?)\s*(minutes?|heures?|semaines?|jours?|mois|années?|ans?)(?=(?:\W|$))/i;

    private TAG: string = 'FRTimeAgoFormatParser';

    private OTHER_PATTERNS: RegExp[] = [
        /\w/
    ];

    private NUM_MATCH: number = 2;
    private UNIT_MATCH: number = 3;

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        if (match.index > 0 && text[match.index - 1].match(this.OTHER_PATTERNS[0])) return null;

        const index: number = match.index + match[1].length;
        const result: ParsedResult = new ParsedResult({
            text: match[0].substr(match[1].length, match[0].length - match[1].length),
            index,
            ref
        });

        result.tags[this.TAG] = true;

        const num: number = matchNumber(match[this.NUM_MATCH]);
        const matchedUnit: UnitOfTime | undefined = matchUnit(match[this.UNIT_MATCH].toLowerCase());
        let mode: Mode = Mode.NONE;
        const momentRef: Moment = moment(ref);

        switch (matchedUnit) {
            case HOUR:
                momentRef.add(-num, 'hour');
                mode = Mode.TIME;
                break;
            case MINUTE:
                momentRef.add(-num, 'minute');
                mode = Mode.TIME;
                break;
            case WEEK:
                momentRef.add(-num, 'week');
                mode = Mode.WEEK;
                break;
            case DAY:
                momentRef.add(-num, 'd');
                mode = Mode.DATE;
                break;
            case MONTH:
                momentRef.add(-num, 'month');
                mode = Mode.DATE;
                break;
            case YEAR:
                momentRef.add(-num, 'year');
                mode = Mode.DATE;
                break;
            default:
                return null;
        }

        switch (mode) {
            case Mode.TIME:
                result.start.imply(DAY, momentRef.date());
                result.start.imply(MONTH, momentRef.month() + 1);
                result.start.imply(YEAR, momentRef.year());
                result.start.assign(HOUR, momentRef.hour());
                result.start.assign(MINUTE, momentRef.minute());
                break;
            case Mode.DATE:
                result.start.assign(DAY, momentRef.date());
                result.start.assign(MONTH, momentRef.month() + 1);
                result.start.assign(YEAR, momentRef.year());
                break;
            case Mode.WEEK:
                result.start.imply(DAY, momentRef.date());
                result.start.imply(MONTH, momentRef.month() + 1);
                result.start.imply(YEAR, momentRef.year());
                result.start.imply(WEEKDAY, momentRef.day());
                break;
            default:
                return null;
        }

        return result;
    }
}

import Parser from '../parser';
import {ParsedComponents, ParsedResult} from "../../result";
import {ParseOptions} from "../../chrono";
import {DAY, HOUR, MERIDIEM, MINUTE, MONTH, SECOND, YEAR} from "../../constants";
import * as moment from "moment";
import {Moment} from "moment";

type Time = [number, number, number, number];

export default class FRTimeExpressionParser extends Parser {
    private PATTERN: RegExp = new RegExp("(^|\\s|T)" +
        "(?:(?:[àa])\\s*)?" +
        "(\\d{1,2}(?:h)?|midi|minuit)" +
        "(?:" +
        "(?:\\.|\\:|\\：|h)(\\d{1,2})(?:m)?" +
        "(?:" +
        "(?:\\:|\\：|m)(\\d{0,2})(?:s)?" +
        ")?" +
        ")?" +
        "(?:\\s*(A\\.M\\.|P\\.M\\.|AM?|PM?))?" +
        "(?=\\W|$)", 'i'
    );

    private END_PATTERN: RegExp = new RegExp("^\\s*" +
        "(\\-|\\–|\\~|\\〜|[àa]|\\?)\\s*" +
        "(\\d{1,2}(?:h)?)" +
        "(?:" +
        "(?:\\.|\\:|\\：|h)(\\d{1,2})(?:m)?" +
        "(?:" +
        "(?:\\.|\\:|\\：|m)(\\d{1,2})(?:s)?" +
        ")?" +
        ")?" +
        "(?:\\s*(A\\.M\\.|P\\.M\\.|AM?|PM?))?" +
        "(?=\\W|$)", 'i'
    );

    private HOUR_GROUP = 2;
    private MINUTE_GROUP = 3;
    private SECOND_GROUP = 4;
    private AM_PM_HOUR_GROUP = 5;

    private TAG: string = 'FRTimeExpressionParser';

    private OTHER_PATTERNS: RegExp[] = [
        /\w/,
        /^\s*([+\-])\s*\d{3,4}$/,
        /^\d+$/
    ];

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        // This pattern can be overlaped Ex. [12] AM, 1[2] AM
        if (match.index > 0 && text[match.index - 1].match(this.OTHER_PATTERNS[0])) return null;

        const refMoment: Moment = moment(ref);

        const result: ParsedResult = new ParsedResult({
            text: match[0].substring(match[1].length),
            index: match.index + match[1].length,
            ref
        });

        result.tags[this.TAG] = true;

        result.start.imply(DAY, refMoment.date());
        result.start.imply(MONTH, refMoment.month() + 1);
        result.start.imply(YEAR, refMoment.year());

        if (!this.extractFirstChunk(result, match)) {
            return null;
        }

        if (!this.extractSecondChunk(text, result) && result.text.match(this.OTHER_PATTERNS[2])) {
            return null;
        }

        return result;
    }

    private extractFirstChunk(result: ParsedResult, match: RegExpExecArray): boolean {
        const time: Time | false = this.getTime(match);
        if (!time) {
            return false;
        }

        let [hour, minute, second, meridiem]: Time = time;

        if (second > -1) {
            result.start.assign(SECOND, second);
        }


        // ----- AM & PM
        if (match[this.AM_PM_HOUR_GROUP]) {
            if (hour > 12) return false;
            const ampm: string = match[this.AM_PM_HOUR_GROUP][0].toLowerCase();
            if (ampm === 'a') {
                meridiem = 0;
                if (hour === 12) hour = 0;
            } else if (ampm === 'p') {
                meridiem = 1;
                if (hour !== 12) hour += 12;
            }
        }

        result.start.assign(HOUR, hour);
        result.start.assign(MINUTE, minute);

        if (meridiem >= 0) {
            result.start.assign(MERIDIEM, meridiem);
        } else {
            if (hour < 12) {
                result.start.imply(MERIDIEM, 0);
            } else {
                result.start.imply(MERIDIEM, 1);
            }
        }

        return true;
    }

    private extractSecondChunk(text: string, result: ParsedResult): boolean {
        const match: RegExpExecArray | null = this.END_PATTERN.exec(text.substring(result.index + result.text.length));

        if (!match) {
            return false;
        }

        // Pattern "YY.YY -XXXX" is more like timezone offset
        if (match[0].match(this.OTHER_PATTERNS[1])) {
            return true;
        }

        const time: Time | false = this.getTime(match);
        if (!time) {
            return true;
        }

        let [hour, minute, second, meridiem]: Time = time;

        if (!result.end) {
            result.end = new ParsedComponents(undefined, result.start.date());
        }

        if (second > -1) {
            result.end.assign(SECOND, second);
        }

        // ----- AM & PM
        if (match[this.AM_PM_HOUR_GROUP]) {

            if (hour > 12) return false;

            const ampm: string = match[this.AM_PM_HOUR_GROUP][0].toLowerCase();
            if (ampm === 'a') {
                meridiem = 0;
                if (hour === 12) {
                    hour = 0;
                    if (!result.end.isCertain(DAY)) {
                        result.end.imply(DAY, result.end.get(DAY) + 1);
                    }
                }
            }

            if (ampm === 'p') {
                meridiem = 1;
                if (hour !== 12) hour += 12;
            }

            if (!result.start.isCertain(MERIDIEM)) {
                if (meridiem === 0) {
                    result.start.imply(MERIDIEM, 0);

                    if (result.start.get(HOUR) === 12) {
                        result.start.assign(HOUR, 0);
                    }
                } else {
                    result.start.imply(MERIDIEM, 1);

                    if (result.start.get(HOUR) !== 12) {
                        result.start.assign(HOUR, result.start.get(HOUR) + 12);
                    }
                }
            }
        }

        result.text = result.text + match[0];
        result.end.assign(HOUR, hour);
        result.end.assign(MINUTE, minute);
        if (meridiem >= 0) {
            result.end.assign(MERIDIEM, meridiem);
        } else {
            const startAtPM: boolean = result.start.isCertain(MERIDIEM) && result.start.get(MERIDIEM) === 1;
            if (startAtPM && result.start.get(HOUR) > hour) {
                // 10pm - 1 (am)
                result.end.imply(MERIDIEM, 0);

            } else if (hour > 12) {
                result.end.imply(MERIDIEM, 1);
            }
        }

        if (result.end.date().getTime() < result.start.date().getTime()) {
            result.end.imply(DAY, result.end.get(DAY) + 1)
        }

        return true;
    }

    private getTime(match: RegExpExecArray): Time | false {
        let second: number = -1;
        let hour: number = 0;
        let minute: number = 0;
        let meridiem: number = -1;

        // ----- Second
        if (match[this.SECOND_GROUP]) {
            second = parseInt(match[this.SECOND_GROUP], 10);
            if (second >= 60) return false;
        }

        // ----- Hours
        if (match[this.HOUR_GROUP].toLowerCase() === 'midi') {
            meridiem = 1;
            hour = 12;
        } else if (match[this.HOUR_GROUP].toLowerCase() === 'minuit') {
            meridiem = 0;
            hour = 0;
        } else {
            hour = parseInt(match[this.HOUR_GROUP], 10);
        }

        // ----- Minutes
        if (match[this.MINUTE_GROUP]) {
            minute = parseInt(match[this.MINUTE_GROUP], 10);
        } else if (hour > 100) {
            minute = hour % 100;
            hour = Math.floor(hour / 100);
        }

        if (minute >= 60) {
            return false;
        }

        if (hour > 24) {
            return false;
        }
        if (hour >= 12) {
            meridiem = 1;
        }

        return [hour, minute, second, meridiem];
    }
}

import Parser from '../parser';
import {ParsedResult} from "../../result";
import {ParseOptions} from "../../chrono";
import {WEEKDAY_OFFSET} from '../../utils/FR';
import {Modifier, updateParsedComponent} from '../../utils/general';

export default class FRWeekdayParser extends Parser {
    private PATTERN: RegExp = new RegExp('(\\s|^)' +
        '(?:(?:\\,|\\(|\\（)\\s*)?' +
        '(?:(ce)\\s*)?' +
        '(' + Object.keys(WEEKDAY_OFFSET).join('|') + ')' +
        '(?:\\s*(?:\\,|\\)|\\）))?' +
        '(?:\\s*(dernier|prochain)\\s*)?' +
        '(?=\\W|$)', 'i');

    private PREFIX_GROUP: number = 2;
    private WEEKDAY_GROUP: number = 3;
    private POSTFIX_GROUP: number = 4;

    private TAG: string = 'FRWeekdayParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index + match[1].length;
        const matchedText: string = match[0].substr(match[1].length, match[0].length - match[1].length);
        const result: ParsedResult = new ParsedResult({
            text: matchedText,
            index,
            ref,
        });

        const offset: number | undefined = WEEKDAY_OFFSET[match[this.WEEKDAY_GROUP].toLowerCase()];
        if (offset === undefined) return null;

        const prefix: string = match[this.PREFIX_GROUP];
        const postfix: string = match[this.POSTFIX_GROUP];
        const norm: string = (prefix || postfix || '').toLowerCase();

        let modifier: Modifier = Modifier.UNKNOWN;
        if (norm== 'ce') {
            modifier = Modifier.THIS;
        } else if (norm === 'dernier') {
            modifier = Modifier.LAST;
        } else if (norm == 'prochain') {
            modifier = Modifier.NEXT;
        }

        result.tags[this.TAG] = true;

        updateParsedComponent(result, ref, offset, modifier);

        return result;
    }
}

import * as moment from "moment";
import {Moment} from "moment";
import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {DAY, HOUR, MERIDIEM, MONTH, YEAR} from "../../constants";

export default class JPCasualDateParser extends Parser {
    private PATTERN: RegExp = /今日|当日|昨日|明日|今夜|今夕|今晩|今朝/i;

    private TAG: string = 'JPCasualDateParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index;
        const result: ParsedResult = new ParsedResult({
            text: match[0],
            index,
            ref
        });

        const refMoment: Moment = moment(ref);
        const startMoment: Moment = refMoment.clone();
        const matchedText = result.text;

        if (matchedText === '今夜' || matchedText === '今夕' || matchedText === '今晩') {
            // Normally means this coming midnight
            result.start.imply(HOUR, 22);
            result.start.imply(MERIDIEM, 1);
        } else if (matchedText === '明日') {
            // Check not "Tomorrow" on late night
            if (refMoment.hour() > 4) {
                startMoment.add(1, 'day');
            }
        } else if (matchedText === '昨日') {
            startMoment.add(-1, 'day');
        } else if (matchedText.match("今朝")) {

            result.start.imply(HOUR, 6);
            result.start.imply(MERIDIEM, 0);
        }

        result.start.assign(DAY, startMoment.date());
        result.start.assign(MONTH, startMoment.month() + 1);
        result.start.assign(YEAR, startMoment.year());
        result.tags[this.TAG] = true;

        return result;
    }
}

import * as moment from "moment";
import {Moment} from "moment";
import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {DAY, MONTH, YEAR} from "../../constants";
import {toHankaku} from "../../utils/JP";
import {getAppropriateYear} from "../../utils/general";

export default class JPStandardParser extends Parser {
    private PATTERN: RegExp = /(?:(同|((昭和|平成)?([0-9０-９]{2,4})))年\s*)?([0-9０-９]{1,2})月\s*([0-9０-９]{1,2})日/i;

    private YEAR_GROUP: number = 2;
    private ERA_GROUP: number = 3;
    private YEAR_NUMBER_GROUP: number = 4;
    private MONTH_GROUP: number = 5;
    private DAY_GROUP: number = 6;

    private OTHER_PATTERNS: RegExp[] = [
        /同年/
    ];

    private TAG: string = 'JPStandardParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index;
        const result: ParsedResult = new ParsedResult({
            text: match[0],
            index,
            ref
        });

        result.tags[this.TAG] = true;
        const startMoment: Moment = moment(ref);

        const month: number = parseInt(toHankaku(match[this.MONTH_GROUP]), 10);
        const day: number = parseInt(toHankaku(match[this.DAY_GROUP]), 10);

        startMoment.set('date', day);
        startMoment.set('month', month - 1);
        result.start.assign(DAY, startMoment.date());
        result.start.assign(MONTH, startMoment.month() + 1);

        const matchedYear: string = match[this.YEAR_GROUP] || '';

        if (matchedYear.match(this.OTHER_PATTERNS[0])) {
            result.start.assign(YEAR, startMoment.year());
        } else if (matchedYear) {
            let year: number = parseInt(toHankaku(match[this.YEAR_NUMBER_GROUP]), 10);
            if (match[this.ERA_GROUP] === '平成') {
                year += 1988;
            } else if (match[this.ERA_GROUP] === '昭和') {
                year += 1925;
            }

            result.start.assign(YEAR, year);
        } else {
            getAppropriateYear(result.start, ref);
        }

        return result;
    }
}

import * as moment from "moment";
import {Moment} from "moment";
import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {DAY, HOUR, MERIDIEM, MILLISECOND, MINUTE, MONTH, SECOND, YEAR} from "../../constants";

export default class ZHHantCasualDateParser extends Parser {
    private PATTERN: RegExp = new RegExp(
        '(而家|立(?:刻|即)|即刻)|' +
        '(今|明|聽|昨|尋|琴)(早|朝|晚)|' +
        '(上(?:午|晝)|朝(?:早)|早(?:上)|下(?:午|晝)|晏(?:晝)|晚(?:上)|夜(?:晚)?|中(?:午)|凌(?:晨))|' +
        '(今|明|聽|昨|尋|琴)(?:日|天)' +
        '(?:[\\s|,|，]*)' +
        '(?:(上(?:午|晝)|朝(?:早)|早(?:上)|下(?:午|晝)|晏(?:晝)|晚(?:上)|夜(?:晚)?|中(?:午)|凌(?:晨)))?', 'i');

    private NOW_GROUP: number = 1;
    private DAY_GROUP_1: number = 2;
    private TIME_GROUP_1: number = 3;
    private TIME_GROUP_2: number = 4;
    private DAY_GROUP_3: number = 5;
    private TIME_GROUP_3: number = 6;


    private TAG: string = 'ZHCasualDateParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index;
        const result: ParsedResult = new ParsedResult({
            text: match[0],
            index,
            ref
        });

        const refMoment: Moment = moment(ref);
        const startMoment: Moment = refMoment.clone();

        if (match[this.NOW_GROUP]) {
            result.start.imply(HOUR, refMoment.hour());
            result.start.imply(MINUTE, refMoment.minute());
            result.start.imply(SECOND, refMoment.second());
            result.start.imply(MILLISECOND, refMoment.millisecond());
        } else if (match[this.DAY_GROUP_1]) {
            const day1: string = match[this.DAY_GROUP_1];
            const time1: string = match[this.TIME_GROUP_1];

            if (day1 === '明' || day1 === '聽') {
                // Check not "Tomorrow" on late night
                if (refMoment.hour() > 1) {
                    startMoment.add(1, 'day');
                }
            } else if (day1 === '昨' || day1 === '尋' || day1 === '琴') {
                startMoment.add(-1, 'day');
            }
            if (time1 === '早' || time1 === '朝') {
                result.start.imply(HOUR, 6);
            } else if (time1 === '晚') {
                result.start.imply(HOUR, 22);
                result.start.imply(MERIDIEM, 1);
            }
        } else if (match[this.TIME_GROUP_2]) {
            const timeString2: string = match[this.TIME_GROUP_2];
            const time2: string = timeString2[0];
            if (time2 === '早' || time2 === '朝' || time2 === '上') {
                result.start.imply(HOUR, 6);
            } else if (time2 === '下' || time2 === '晏') {
                result.start.imply(HOUR, 15);
                result.start.imply(MERIDIEM, 1);
            } else if (time2 === '中') {
                result.start.imply(HOUR, 12);
                result.start.imply(MERIDIEM, 1);
            } else if (time2 === '夜' || time2 === '晚') {
                result.start.imply(HOUR, 22);
                result.start.imply(MERIDIEM, 1);
            } else if (time2 === '凌') {
                result.start.imply(HOUR, 0);
            }

        } else if (match[this.DAY_GROUP_3]) {
            const day3: string = match[this.DAY_GROUP_3];

            if (day3 === '明' || day3 === '聽') {
                // Check not "Tomorrow" on late night
                if (refMoment.hour() > 1) {
                    startMoment.add(1, 'day');
                }
            } else if (day3 === '昨' || day3 === '尋' || day3 === '琴') {
                startMoment.add(-1, 'day');
            }


            const timeString3: string = match[this.TIME_GROUP_3];
            if (timeString3) {
                const time3: string = timeString3[0];
                if (time3 === '早' || time3 === '朝' || time3 === '上') {
                    result.start.imply(HOUR, 6);
                } else if (time3 === '下' || time3 === '晏') {
                    result.start.imply(HOUR, 15);
                    result.start.imply(MERIDIEM, 1);
                } else if (time3 === '中') {
                    result.start.imply(HOUR, 12);
                    result.start.imply(MERIDIEM, 1);
                } else if (time3 === '夜' || time3 === '晚') {
                    result.start.imply(HOUR, 22);
                    result.start.imply(MERIDIEM, 1);
                } else if (time3 === '凌') {
                    result.start.imply(HOUR, 0);
                }
            }
        }

        result.start.assign(DAY, startMoment.date());
        result.start.assign(MONTH, startMoment.month() + 1);
        result.start.assign(YEAR, startMoment.year());
        result.tags[this.TAG] = true;

        return result;
    }
}

import * as moment from "moment";
import {Moment} from "moment";
import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {DAY, MONTH, YEAR} from "../../constants";
import {NUMBER, zhStringToNumber, zhStringToYear} from "../../utils/ZH-Hant";

export default class ZHHantDateParser extends Parser {
    private PATTERN: RegExp = new RegExp(
        '(\\d{2,4}|[' + Object.keys(NUMBER).join('') + ']{2,4})?' +
        '(?:\\s*)' +
        '(?:年)?' +
        '(?:[\\s|,|，]*)' +
        '(\\d{1,2}|[' + Object.keys(NUMBER).join('') + ']{1,2})' +
        '(?:\\s*)' +
        '(?:月)' +
        '(?:\\s*)' +
        '(\\d{1,2}|[' + Object.keys(NUMBER).join('') + ']{1,2})?' +
        '(?:\\s*)' +
        '(?:日|號)?'
    );

    private YEAR_GROUP: number = 1;
    private MONTH_GROUP: number = 2;
    private DAY_GROUP: number = 3;


    private TAG: string = 'ZHDateParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index;
        const result: ParsedResult = new ParsedResult({
            text: match[0],
            index,
            ref
        });

        result.tags[this.TAG] = true;

        const startMoment: Moment = moment(ref);
        const matchedMonth = match[this.MONTH_GROUP];
        const matchedDay = match[this.DAY_GROUP];
        const matchedYear = match[this.YEAR_GROUP];

        let month: number = parseInt(matchedMonth, 10);
        if (isNaN(month)) {
            month = zhStringToNumber(matchedMonth);
        }

        result.start.assign(MONTH, month);

        if (matchedDay) {
            let day: number = parseInt(matchedDay, 10);
            if (isNaN(day)) {
                day = zhStringToNumber(matchedDay);
            }
            result.start.assign(DAY, day);
        } else {
            result.start.assign(DAY, startMoment.date());
        }

        if (matchedYear) {
            let year: number = parseInt(matchedYear, 10);
            if (isNaN(year)) {
                year = zhStringToYear(matchedYear);
            }
            result.start.assign(YEAR, year);
        } else {
            result.start.assign(YEAR, startMoment.year());
        }

        return result;
    }
}

import * as moment from "moment";
import {Moment} from "moment";
import Parser from '../parser';
import {ParsedResult} from '../../result';
import {ParseOptions} from "../../chrono";
import {DAY, HOUR, MINUTE, MONTH, SECOND, YEAR} from "../../constants";
import {NUMBER, zhStringToNumber} from "../../utils/ZH-Hant";

export default class ZHHantDeadlineFormatParser extends Parser {
    private PATTERN: RegExp = new RegExp(
        '(\\d+|[' + Object.keys(NUMBER).join('') + ']+|半|幾)(?:\\s*)' +
        '(?:個)?' +
        '(秒(?:鐘)?|分鐘|小時|鐘|日|天|星期|禮拜|月|年)' +
        '(?:(?:之|過)?後|(?:之)?內)', 'i'
    );

    private NUMBER_GROUP: number = 1;
    private UNIT_GROUP: number = 2;


    private TAG: string = 'ZHDeadlineFormatParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index;
        const result: ParsedResult = new ParsedResult({
            text: match[0],
            index,
            ref
        });

        result.tags[this.TAG] = true;

        const matchedNumber: string = match[this.NUMBER_GROUP];
        let number: number = parseInt(matchedNumber, 10);

        if (isNaN(number)) {
            number = zhStringToNumber(matchedNumber);
            if (isNaN(number)) {
                if (matchedNumber === '幾') {
                    number = 3;
                } else if (matchedNumber === '半') {
                    number = 0.5;
                } else {
                    return null;
                }
            }
        }

        const date: Moment = moment(ref);
        const unitAbbr: string = match[this.UNIT_GROUP][0];
        let dateMode: boolean = false;

        switch (unitAbbr) {
            case '日':
            case '天':
                date.add(number, 'd');
                dateMode = true;
                break;
            case '星':
            case '禮':
                date.add(number * 7, 'd');
                dateMode = true;
                break;
            case '月':
                date.add(number, 'month');
                dateMode = true;
                break;
            case '年':
                date.add(number, 'year');
                dateMode = true;
                break;
            case '秒':
                date.add(number, 'second');
                break;
            case '分':
                date.add(number, 'minute');
                break;
            case '小':
            case '鐘':
                date.add(number, 'hour');
                break;
            default:
                return null;
        }

        if (dateMode) {
            result.start.assign(YEAR, date.year());
            result.start.assign(MONTH, date.month() + 1);
            result.start.assign(DAY, date.date());
        } else {
            result.start.imply(YEAR, date.year());
            result.start.imply(MONTH, date.month() + 1);
            result.start.imply(DAY, date.date());
            result.start.assign(HOUR, date.hour());
            result.start.assign(MINUTE, date.minute());
            result.start.assign(SECOND, date.second());
        }

        return result;
    }
}

import Parser from '../parser';
import {ParsedComponents, ParsedResult} from "../../result";
import {ParseOptions} from "../../chrono";
import {DAY, HOUR, MERIDIEM, MINUTE, MONTH, SECOND, YEAR} from "../../constants";
import * as moment from "moment";
import {Moment} from "moment";
import {NUMBER, zhStringToNumber} from "../../utils/ZH-Hant";

type Time = [number, number, number, number];

export default class ZHHantTimeExpressionParser extends Parser {
    private PATTERN: RegExp = new RegExp('(?:由|從|自)?' +
        '(?:' +
        '(今|明|聽|昨|尋|琴)(早|朝|晚)|' +
        '(上(?:午|晝)|朝(?:早)|早(?:上)|下(?:午|晝)|晏(?:晝)|晚(?:上)|夜(?:晚)?|中(?:午)|凌(?:晨))|' +
        '(今|明|聽|昨|尋|琴)(?:日|天)' +
        '(?:[\\s,，]*)' +
        '(?:(上(?:午|晝)|朝(?:早)|早(?:上)|下(?:午|晝)|晏(?:晝)|晚(?:上)|夜(?:晚)?|中(?:午)|凌(?:晨)))?' +
        ')?' +
        '(?:[\\s,，]*)' +
        '(?:(\\d+|[' + Object.keys(NUMBER).join('') + ']+)(?:\\s*)(?:點|時|:|：)' +
        '(?:\\s*)' +
        '(\\d+|半|正|整|[' + Object.keys(NUMBER).join('') + ']+)?(?:\\s*)(?:分|:|：)?' +
        '(?:\\s*)' +
        '(\\d+|[' + Object.keys(NUMBER).join('') + ']+)?(?:\\s*)(?:秒)?)' +
        '(?:\\s*(A\.M\.|P\.M\.|AM?|PM?))?', 'i');

    private END_PATTERN: RegExp = new RegExp('(?:\\s*(?:到|至|\\-|\\–|\\~|\\〜)\\s*)' +
        '(?:' +
        '(今|明|聽|昨|尋|琴)(早|朝|晚)|' +
        '(上(?:午|晝)|朝(?:早)|早(?:上)|下(?:午|晝)|晏(?:晝)|晚(?:上)|夜(?:晚)?|中(?:午)|凌(?:晨))|' +
        '(今|明|聽|昨|尋|琴)(?:日|天)' +
        '(?:[\\s,，]*)' +
        '(?:(上(?:午|晝)|朝(?:早)|早(?:上)|下(?:午|晝)|晏(?:晝)|晚(?:上)|夜(?:晚)?|中(?:午)|凌(?:晨)))?' +
        ')?' +
        '(?:[\\s,，]*)' +
        '(?:(\\d+|[' + Object.keys(NUMBER).join('') + ']+)(?:\\s*)(?:點|時|:|：)' +
        '(?:\\s*)' +
        '(\\d+|半|正|整|[' + Object.keys(NUMBER).join('') + ']+)?(?:\\s*)(?:分|:|：)?' +
        '(?:\\s*)' +
        '(\\d+|[' + Object.keys(NUMBER).join('') + ']+)?(?:\\s*)(?:秒)?)' +
        '(?:\\s*(A\.M\.|P\.M\.|AM?|PM?))?', 'i');

    private DAY_GROUP_1: number = 1;
    private ZH_AM_PM_HOUR_GROUP_1: number = 2;
    private ZH_AM_PM_HOUR_GROUP_2: number = 3;
    private DAY_GROUP_3: number = 4;
    private ZH_AM_PM_HOUR_GROUP_3: number = 5;
    private HOUR_GROUP: number = 6;
    private MINUTE_GROUP: number = 7;
    private SECOND_GROUP: number = 8;
    private AM_PM_HOUR_GROUP: number = 9;

    private TAG: string = 'ZHTimeExpressionParser';

    private OTHER_PATTERNS: RegExp[] = [
        /\w/,
        /^\s*([+\-])\s*\d{3,4}$/,
        /^\d+$/
    ];

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        // This pattern can be overlaped Ex. [12] AM, 1[2] AM
        if (match.index > 0 && text[match.index - 1].match(this.OTHER_PATTERNS[0])) return null;

        const refMoment: Moment = moment(ref);

        const result: ParsedResult = new ParsedResult({
            text: match[0],
            index: match.index,
            ref
        });

        result.tags[this.TAG] = true;

        result.start.imply(DAY, refMoment.date());
        result.start.imply(MONTH, refMoment.month() + 1);
        result.start.imply(YEAR, refMoment.year());

        const startMoment: Moment = refMoment.clone();

        if (!this.extractFirstChunk(result, match, refMoment, startMoment)) {
            return null;
        }

        const endMoment = startMoment.clone();

        if (!this.extractSecondChunk(text, result, refMoment, endMoment) && result.text.match(this.OTHER_PATTERNS[2])) {
            return null;
        }

        return result;
    }

    private extractFirstChunk(result: ParsedResult, match: RegExpExecArray, refMoment: Moment, startMoment: Moment): boolean {
        this.getDay(match, result.start, refMoment, startMoment);

        const time: Time | false = this.getTime(match);
        if (!time) {
            return false;
        }

        let [hour, minute, second, meridiem]: Time = time;

        if (second > -1) {
            result.start.assign(SECOND, second);
        }

        const gotMeridiem: [number, number] | null = this.getMeridiem(match, meridiem, hour);
        if (!gotMeridiem) {
            return false;
        }

        [meridiem, hour] = gotMeridiem;

        result.start.assign(HOUR, hour);
        result.start.assign(MINUTE, minute);

        if (meridiem >= 0) {
            result.start.assign(MERIDIEM, meridiem);
        } else {
            if (hour < 12) {
                result.start.imply(MERIDIEM, 0);
            } else {
                result.start.imply(MERIDIEM, 1);
            }
        }

        return true;
    }

    private extractSecondChunk(text: string, result: ParsedResult, refMoment: Moment, endMoment: Moment): boolean {
        const match: RegExpExecArray | null = this.END_PATTERN.exec(text.substring(result.index + result.text.length));

        if (!match) {
            return false;
        }

        // Pattern "YY.YY -XXXX" is more like timezone offset
        if (match[0].match(this.OTHER_PATTERNS[1])) {
            return true;
        }

        if (!result.end) {
            result.end = new ParsedComponents(undefined, result.start.date());
        }

        this.getDay(match, result.end, refMoment, endMoment);

        const time: Time | false = this.getTime(match);
        if (!time) {
            return true;
        }

        let [hour, minute, second, meridiem]: Time = time;

        if (second > -1) {
            result.end.assign(SECOND, second);
        }

        const gotMeridiem: [number, number] | null = this.getMeridiem(match, meridiem, hour);
        if (!gotMeridiem) {
            return false;
        }

        [meridiem, hour] = gotMeridiem;

        result.text = result.text + match[0];
        result.end.assign(HOUR, hour);
        result.end.assign(MINUTE, minute);
        if (meridiem >= 0) {
            result.end.assign(MERIDIEM, meridiem);
        } else {
            const startAtPM: boolean = result.start.isCertain(MERIDIEM) && result.start.get(MERIDIEM) === 1;
            if (startAtPM && result.start.get(HOUR) > hour) {
                // 10pm - 1 (am)
                result.end.imply(MERIDIEM, 0);

            } else if (hour > 12) {
                result.end.imply(MERIDIEM, 1);
            }
        }

        if (result.end.date().getTime() < result.start.date().getTime()) {
            result.end.imply(DAY, result.end.get(DAY) + 1)
        }

        return true;
    }

    private getTime(match: RegExpExecArray): Time | false {
        let second: number = -1;
        let hour: number;
        let minute: number = 0;
        let meridiem: number = -1;

        // ----- Second
        if (match[this.SECOND_GROUP]) {
            second = parseInt(match[this.SECOND_GROUP], 10);
            if (isNaN(second)) {
                second = zhStringToNumber(match[this.SECOND_GROUP]);
            }
            if (second >= 60) return false;
        }

        // ----- Hours
        hour = parseInt(match[this.HOUR_GROUP], 10);
        if (isNaN(hour)) {
            hour = zhStringToNumber(match[this.HOUR_GROUP]);
        }

        // ----- Minutes
        if (match[this.MINUTE_GROUP]) {
            if (match[this.MINUTE_GROUP] === '半') {
                minute = 30;
            } else if (match[this.MINUTE_GROUP] === '正' || match[this.MINUTE_GROUP] === '整') {
                minute = 0;
            } else {
                minute = parseInt(match[this.MINUTE_GROUP], 10);
                if (isNaN(minute)) {
                    minute = zhStringToNumber(match[this.MINUTE_GROUP]);
                }
            }
        } else if (hour > 100) {
            minute = hour % 100;
            hour = Math.floor(hour / 100);
        }

        if (minute >= 60) {
            return false;
        }

        if (hour > 24) {
            return false;
        }
        if (hour >= 12) {
            meridiem = 1;
        }

        return [hour, minute, second, meridiem];
    }

    private getDay(match: RegExpExecArray, current: ParsedComponents, refMoment: Moment, targetMoment: Moment): void {
        // ----- Day
        if (match[this.DAY_GROUP_1]) {
            const day1: string = match[this.DAY_GROUP_1];
            if (day1 === '明' || day1 === '聽') {
                // Check not "Tomorrow" on late night
                if(refMoment.hour() > 1) {
                    targetMoment.add(1, 'day');
                }
            } else if (day1 === '昨' || day1 === '尋' || day1 === '琴') {
                targetMoment.add(-1, 'day');
            }
            current.assign(DAY, targetMoment.date());
            current.assign(MONTH, targetMoment.month() + 1);
            current.assign(YEAR, targetMoment.year());
        } else if (match[this.DAY_GROUP_3]) {
            const day3: string = match[this.DAY_GROUP_3];
            if (day3 === '明' || day3 === '聽') {
                targetMoment.add(1, 'day');
            } else if (day3 === '昨' || day3 === '尋' || day3 === '琴') {
                targetMoment.add(-1, 'day');
            }
            current.assign(DAY, targetMoment.date());
            current.assign(MONTH, targetMoment.month() + 1);
            current.assign(YEAR, targetMoment.year());
        } else {
            current.imply(DAY, targetMoment.date());
            current.imply(MONTH, targetMoment.month() + 1);
            current.imply(YEAR, targetMoment.year());
        }
    }

    private getMeridiem(match: RegExpExecArray, meridiem: number, hour: number): [number, number] | null {
        // ----- AM & PM
        if (match[this.AM_PM_HOUR_GROUP]) {
            if (hour > 12) return null;
            const ampm: string = match[this.AM_PM_HOUR_GROUP][0].toLowerCase();
            if (ampm === 'a') {
                meridiem = 0;
                if (hour === 12) hour = 0;
            } else if (ampm === 'p') {
                meridiem = 1;
                if (hour !== 12) hour += 12;
            }
        } else if (match[this.ZH_AM_PM_HOUR_GROUP_1]) {
            const ampm: string = match[this.ZH_AM_PM_HOUR_GROUP_1][0];
            if (ampm === '朝' || ampm === '早') {
                meridiem = 0;
                if (hour === 12) hour = 0;
            } else if (ampm === '晚') {
                meridiem = 1;
                if (hour !== 12) hour += 12;
            }
        } else if (match[this.ZH_AM_PM_HOUR_GROUP_2]) {
            const ampm: string = match[this.ZH_AM_PM_HOUR_GROUP_2][0];
            if (ampm === '上' || ampm === '朝' || ampm === '早' || ampm === '凌') {
                meridiem = 0;
                if (hour === 12) hour = 0;
            } else if (ampm === '下' || ampm === '晏' || ampm === '晚') {
                meridiem = 1;
                if (hour !== 12) hour += 12;
            }
        } else if (match[this.ZH_AM_PM_HOUR_GROUP_3]) {
            const ampm: string = match[this.ZH_AM_PM_HOUR_GROUP_3][0];
            if (ampm === '上' || ampm === '朝' || ampm === '早' || ampm === '凌') {
                meridiem = 0;
                if (hour === 12) hour = 0;
            } else if (ampm === '下' || ampm === '晏' || ampm === '晚') {
                meridiem = 1;
                if (hour !== 12) hour += 12;
            }
        }

        return [meridiem, hour];
    }
}

import Parser from '../parser';
import {ParsedResult} from "../../result";
import {ParseOptions} from "../../chrono";
import {WEEKDAY_OFFSET} from '../../utils/ZH-Hant';
import {Modifier, updateParsedComponent} from '../../utils/general';

export default class ZHHantWeekdayParser extends Parser {
    private PATTERN: RegExp = new RegExp(
        '(上|今|下|這|呢)?' +
        '(?:個)?' +
        '(?:星期|禮拜)' +
        '(' + Object.keys(WEEKDAY_OFFSET).join('|') + ')'
    );

    private PREFIX_GROUP: number = 1;
    private WEEKDAY_GROUP: number = 2;

    private TAG: string = 'ZHWeekdayParser';

    pattern(): RegExp {
        return this.PATTERN;
    }

    extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null {
        const index: number = match.index;
        const matchedText: string = match[0];
        const result: ParsedResult = new ParsedResult({
            text: matchedText,
            index,
            ref,
        });

        const offset: number | undefined = WEEKDAY_OFFSET[(match[this.WEEKDAY_GROUP] || '').toLowerCase()];
        if (offset === undefined) return null;

        const norm: string = (match[this.PREFIX_GROUP] || '').toLowerCase();

        let modifier: Modifier = Modifier.UNKNOWN;
        if (norm == '今' || norm == '這' || norm == '呢') {
            modifier = Modifier.THIS;
        } else if (norm === '上') {
            modifier = Modifier.LAST;
        } else if (norm == '下') {
            modifier = Modifier.NEXT;
        }

        result.tags[this.TAG] = true;

        updateParsedComponent(result, ref, offset, modifier);

        return result;
    }
}

import {Config} from "../options";
import {ParseOptions} from "../chrono";
import {ParsedResult} from "../result";

export default abstract class Parser {
    strictMode: boolean;

    constructor(config: Config = {}) {
        this.strictMode = !!config.strict;
    }

    isStrictMode(): boolean {
        return this.strictMode;
    }

    abstract pattern(): RegExp;

    abstract extract(text: string, ref: Date, match: RegExpExecArray, opt: ParseOptions): ParsedResult | null;

    execute(text: string, ref: Date, opt: ParseOptions): ParsedResult[] {

        const results: ParsedResult[] = [];
        const regex: RegExp = this.pattern();

        let remainingText: string = text;
        let match: RegExpExecArray | null = regex.exec(remainingText);


        while (match) {
            // Calculate match index on the full text;
            match.index += text.length - remainingText.length;

            let result: ParsedResult | null = this.extract(text, ref, match, opt);
            if (result) {
                // If success, start from the end of the result
                remainingText = text.substring(result.index + result.text.length);

                if (!this.isStrictMode() || result.hasPossibleDates()) {
                    results.push(result);
                }
            } else {
                // If fail, move on by 1 - This should rarely, if ever happen (we matched but couldn't create a result from it)
                remainingText = text.substring(match.index + 1);
            }

            match = regex.exec(remainingText);
        }

        return results;
    }
}

