const V_REGEX: RegExp = /v/i;

export const yearCalculation = (year: string, yearBe: string): number | null => {
    if (year) {
        let result: number = parseInt(year, 10);

        if (yearBe) {
            if (V_REGEX.test(yearBe)) {
                // v.Chr.
                result = -result;
            }

            return result;
        } else {
            if (result < 100) {
                result = result + 2000;
            }
            return result;
        }
    }

    return null;
};

