const TIME_UNIT: string =
    '(' + INTEGER_WORDS_PATTERN + '|[0-9]+|an?(?:\\s*few)?|half(?:\\s*an?)?)\\s*' +
    '(sec(?:onds?)?|min(?:ute)?s?|hours?|weeks?|days?|months?|years?)\\s*';

const TIME_UNIT_STRICT: string =
    '([0-9]+|an?)\\s*' +
    '(seconds?|minutes?|hours?|days?)\\s*';

const PATTERN_TIME_UNIT: RegExp = new RegExp(TIME_UNIT, 'i');

export const TIME_UNIT_PATTERN: string = '(?:' + TIME_UNIT + ')+';
export const TIME_UNIT_STRICT_PATTERN: string = '(?:' + TIME_UNIT_STRICT + ')+';

export type FragmentName = 'hour' |
    'minute' |
    'second' |
    'week' |
    'd' |
    'month' |
    'year';

export type FragmentMap = {
    [k in FragmentName]?: number
}

export const extractDateTimeUnitFragments = (timeunitText: string): FragmentMap => {
    const fragments: FragmentMap = {};
    let remainingText: string = timeunitText;
    let match: RegExpExecArray | null = PATTERN_TIME_UNIT.exec(remainingText);
    while (match) {
        collectDateTimeFragment(match, fragments);
        remainingText = remainingText.substring(match[0].length);
        match = PATTERN_TIME_UNIT.exec(remainingText);
    }
    return fragments;
};

function collectDateTimeFragment(match: RegExpExecArray, fragments: FragmentMap) {
    const matchedNumber = match[1].toLowerCase();
    const matchedUnit = match[2].toLowerCase();
    let num: number = 0;
    if (INTEGER_WORDS.hasOwnProperty(matchedNumber)) {
        num = INTEGER_WORDS[matchedNumber];
    } else if (matchedNumber === 'a' || matchedNumber === 'an') {
        num = 1;
    } else if (matchedNumber.match(/few/)) {
        num = 3;
    } else if (matchedNumber.match(/half/)) {
        num = 0.5;
    } else {
        num = parseInt(matchedNumber, 10);
    }

    if (matchedUnit.match(/hour/)) {
        fragments['hour'] = num;
    } else if (matchedUnit.match(/min/)) {
        fragments['minute'] = num;
    } else if (matchedUnit.match(/sec/)) {
        fragments['second'] = num;
    } else if (matchedUnit.match(/week/)) {
        fragments['week'] = num;
    } else if (matchedUnit.match(/day/)) {
        fragments['d'] = num;
    } else if (matchedUnit.match(/month/)) {
        fragments['month'] = num;
    } else if (matchedUnit.match(/year/)) {
        fragments['year'] = num;
    }

    return fragments;
}

const HALF_REGEX: RegExp = /half/;
const FEW_REGEX: RegExp = /few/;

export const matchInteger = (text: string): number => {
    let num: number = 0;
    if (text) {
        if (INTEGER_WORDS.hasOwnProperty(text)) {
            num = INTEGER_WORDS[text];
        } else if (text === 'a' || text === 'an') {
            num = 1;
        } else if (text.match(FEW_REGEX)) {
            num = 3;
        } else if (text.match(HALF_REGEX)) {
            num = 0.5;
        } else {
            num = parseInt(text, 10);
        }
    }

    return num;
};

